insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (619,'【青田县】主导、参与起草制定各级标准','2024-01-10','2024-12-31','承担全国专业标准化技术委员会秘书处、全国专业标准化分技术委员会秘书处、浙江省标准化技术委员会秘书处、浙江省标准化分技术委员会秘书处、标准化技术委员会秘书处、标准化分技术委员会秘书处的单位，分别一次性奖励50万元、40万元、30万元、20万元、10万元、5万元；国际标准、国家标准、行业标准、省地方标准的主导起草单位，分别奖励100万元、50万元、20万元、10万元；国际标准、国家标准、行业标准、省地方标准的参与起草单位，分别奖励30万元、10万元、5万元、3万元；主导、参与制定团体标准的，分别奖励5万元、3万元。\n相关政策',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-46566',NULL,'0578-6961766','青田县市场监督管理局质量与标准监督管理科',0,0,0,NULL,'市（区、县）人民政府',NULL,'企业',NULL,0,NULL,'《人民政府关于印发的通知》（青政发〔2024〕5号）','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (620,'【青田县】非转让获得国内外专利授权分类别补助','2024-01-10','2024-12-31','2022年度知识产权奖补调整如下：1.取消对实用新型和外观设计专利的授权补助；2.发明专利的授权补助统一调整到每件2000元；3.知识产权质押融资合同执行完毕的，按青政发〔2020〕97号文件执行；4.其他奖补项目不变。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-46565',NULL,'0578-6827338','青田县市场监督管理局知识产权监督管理科',0,0,0,NULL,'市（区、县）人民政府',NULL,'符合实施办法中奖励条款规定的单位、组织或个人。',NULL,0,NULL,'地方现有资助的范围应限于获得授权的发明专利（包括通过PCT及其他途径在境外获得授权的发明专利），资助方式应采用授权后补助形式。资助对象所获得的各级各类资助总额不得高于其获得专利权所缴纳的官方规定费用的50%，不得资助专利年费和专利代理等中介服务费。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (621,'【青田县】企业研发费奖励','2024-01-10','2024-12-31','鼓励企业加大研发投入。对年度研发费用超过50万元、年增长10%（含）以上且研发费用占营业收入比重3%（含）以上的企业（不包括首次清零），对较前一年度研发费增量部分按10%比例给予奖励，基础部分按1%比例给予奖励，属于高新技术企业、“专精特新”企业的，增量部分奖励比例提高至15%，属于省科技小巨人、省科技领军企业的，增量部分奖励比例提高至20%，单家企业累计奖励不超过100万元。研发费年增长10%以下或研发费占营业收入比重3%以下企业研发费补助减半享受。对研发费首次清零且达到50万元、200万元、500万元、1000万元的企业(含县外引进、迁入)，分别给予2万元、8万元、15万元、30万元奖励。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-46268',NULL,'0578-6816080','青田县科学技术局青田县科技创新服务中心',0,0,0,NULL,'市（区、县）人民政府',NULL,'企业',NULL,0,NULL,'对财务核算制度健全，科技经费使用合理规范并实行单独建账列支的企业，其年度研发费用由县税务主管部门提供数据，经县科技局核定，享受研发后补助。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (622,'【庆元县】《关于进一步促进商贸服务等业态发展和引导庆商回归的实施意见》','2024-05-16',NULL,'为进一步促进商贸服务等业态发展，拓展庆商回归领域，根据《庆元县“地瓜经济”提能升级“一号开放工程”实施方案（2023—2027）》等文件精神，围绕“引进一批、回归一批、培育一批”的发展思路，结合我县实际，制定本意见。\n二、扶持政策\n\n（一）采用量分制综合评估考核企业，再按分值奖励。\n\n入驻企业签订《招商引资协议》之日起可享受五年扶持奖励，扶持奖励与入驻企业的考评结果得分挂钩。由招引专班和领导小组办对企业进行年度和季度考核。季度考核以每个季度为一个计算周期。年度考核以公历年度（或合同签订日起12个月）为一个计算周期，每满一个计算周期进行年度考核。\n\n1.每引进1家首次考评结果得分为60分的企业，可享受基础奖励51万元，考评结果得分每增加1分再奖励23万元，考评结果分值在相对应区间内的用插入法计算；以后季度按季度考评结果得分增加部分给予核算奖励。\n\n2.每引进1家首次考评结果得分为50分以上60以下的企业，可享受基础奖励3万元，50分以上得分每增加1分再奖励5万元，50分以下的企业不再奖励，得分在相对应区间内的用插入法计算；以后季度按上季度考评结果得分增加部分给予核算奖励。在首次考评的一年内，当考评结果得分首次达到60分及以上的企业，奖励按本条款第1条执行，并扣减原每季度奖励的部分。\n\n3.每满一个年度计算周期，进行运营年度考核并按入驻企业考评结果得分结算运营扶持奖励。\n\n（二）平台型企业（机构）其招引落地的相关企业（机构）形成的指标数据，经入驻决策评价工作领导小组审核确认后可合并平台型企业（机构）指标，享受相应的奖励政策。\n\n（三）企业（机构）因经营需要在我县租用办公场所的，租期内租金按先缴后返的形式予以补助：考评结果得分达50分的，按100元/㎡/年安排；考评结果得分达60分的，按200元/㎡/年安排。补助面积不超过200平方米，超过部分不予补助。\n\n（四）对企业（机构）招引具有重大贡献的单位或个人，由入驻决策评价工作领导小组推荐，经相关程序可以评选“特别贡献奖”，结合实际贡献，给予1-30万元奖励。\n\n（五）对新引进境内外上市公司、中央各部委直属及省属国资企业或区域总部，股权投资类，及其他有特殊情况的企业可实行另行商议。',0,'https://www.zjqy.gov.cn/art/2024/5/16/art_1229428723_2519969.html',NULL,'0578-6059881','庆元县经济合作交流中心',0,0,0,NULL,'经济合作交流中心',NULL,'政策认定的主体',NULL,0,NULL,'在庆元县行政区域范围内注册或迁入的企业，依法开展经营活动，符合国家、省、市和庆元县产业发展政策导向，具有独立法人资格的企业（机构），可依企业申请经庆元县入驻决策评价工作领导小组评审认定。引进企业以数字经济、文创、科创、电商、金融、商贸服务等产业主导方向及其它符合条件的企业。如新引进批零商贸类企业,具备外商投资的企业,各类跨境电子商务实体企业,各类产品质量控制，检验检测等第三方实体机构，对地方产业发展具有贡献的企业,有资质建筑类企业等。','',NULL);
