insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (624,'【庆元县】支持电气保供','2024-02-07','2024-12-31','加强对春节期间不停产企业的生产保障，对春节期间连续生产的规上制造业企业用电予以分档补助，企业春节期间日均用电量达到 2023 年全年日均用电量的 50%、70%、90%以上的，分别给予其春节期间用电每千瓦时 0.3 元、0.4 元、0.5元的补助，最高补助 10 万元；协调供气企业在保证安全生产的前提下，视用气企业实际需要适当延长春节前后供气时间。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-52956',NULL,'0578-60582312','丽水市庆元县经济商务局经济运行科',0,0,0,NULL,'经济发展协调推进小组办公室',NULL,'庆元县域内民营的工业企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (625,'【庆元县】鼓励企业增效','2024-02-07','2024-12-31','全面激励中小企业提质增效、做大做强，对重点培育的规下制造业企业给予分档奖励，1-2 月营业收入达 50万元以上且同比增长 20%、30%及以上的，分别给予 0.5 万元、1万元的奖励；2023 年新注册企业 2024 年 1-2 月营业收入达到 50万元、150 万元及以上的分别给予 0.5 万元、1 万元的奖励；对一季度经过认定新增纳入重点区域培育清单的规下企业额外给予 1 万元的奖励。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-52955',NULL,'0578-6058231','丽水市庆元县经济商务局经济运行科',0,0,0,NULL,'经济发展协调推进小组办公室',NULL,'庆元县域内民营的工业企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (626,'【庆元县】鼓励工业增产','2024-02-07','2024-12-31','鼓励工业企业连续开工，开展复产达产专项服务活动，帮助企业解决生产组织中的困难问题，促进稳产增产，勇夺一季度“开门红”，对一季度保持较快增长的规上工业企业，按档分别给予奖励，同时对战略性新兴产业企业予以奖励上浮，最高奖励20万元。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-52954',NULL,'0578-6058231','丽水市庆元县经济商务局经济运行科',0,0,0,NULL,'经济发展协调推进小组办公室',NULL,'庆元县域内民营的工业企业',NULL,0,NULL,'对一季度营业收入正增长的规上工业企业，①一季度累计产值总量在3000万元以上且同比增长5%、10%及以上的，以上年产值同期数为基准，分别给予超基准部分5‰、8‰的奖励；②一季度累计产值在1000-3000万元且同比增长15%、30%及以上的，以上年产值同期数为基准，分别给予超基准部分5‰、8‰的奖励；2023年月度升规企业2024年一季度累计产值达到1000万元、2000万元及以上的，以500万元为基准，分别给予超基准部分5‰、8‰的奖励。战略性新兴产业企业在原有奖励基础上上浮2‰。单家企业奖励最高不超过20万元。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (627,'【庆元县】庆元县加快工业项目开工进度奖励','2024-06-14','2024-11-30','为鼓励企业加快开工建设，根据企业取得土地时间及开工建设进度，按《建设工程规划许可证》计容建筑面积（下同）分类别给予生产用房面积 30 元/平方米、非生产用房面积 27元/平方米奖励。奖励以万元为单位，保留一位小数，低于 1千元不予奖励。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-54367',NULL,'13906880275','丽水市庆元县经济商务局投资与信息化科',0,0,0,NULL,'生态工业高质量绿色发展工作领导小组办公室',NULL,'庆元县域内企业',NULL,0,NULL,'奖励条件：1.2023 年 1 月 20 日之前签订国有土地出让合同的供地项目，在约定开工时间前 1 个月（含，下同）以上取得《建筑工程施工许可证》的，正式开工建设并形成投资量后，按政策标准一次性给予奖励。其中因客观原因办理延期手续的项目，以延期手续约定开工时间计算。2.2023 年 1 月 21 日至 2024 年 6 月 11 日期间签订国有土地出让合同的供地项目，在约定开工时间前 3 个月以上取得《建筑工程施工许可证》的，正式开工建设并形成投资量后，按政策标准一次性给予奖励。3.通过改扩建实施的项目，在 2024 年 6 月 11 日之前取得《建设工程规划许可证》的，正式开工建设并形成投资量后，按新增的计容建筑面积（同一地块建筑工程规划许可证计容面积-原有不动产权证面积），按奖励标准一次性给予奖励。上述奖励资金由县财政配套，若项目建设过程中变更《建设工程规划许可证》，发生计容建筑面积变化的，按变更后的《建筑工程规划许可证》计容建筑面积予以增补或追缴；相关部门应保障出让地块具备开工条件，确因条件不具备影响项目开的，企业有权就奖励政策享受提出申诉，由县经开区、县自然资源和规划局、县住建局、县经商局、县经合中心等部门联合研究奖励认定。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (628,'【庆元县】2024年结对促共富示范创建财政专项激励资金（第一批）','2024-10-10','2024-11-30','一、“产业促共富”重点企业租金补贴。生物科技产业园范围内入驻的“产业促共富”重点企业租赁厂房（2024年1月1日之后取得发票），以县经投公司与承租企业签订的租赁合同计算，给予承租企业不超过12个月租金补贴，具体补助比例按照招商引资协议执行。二、机器设备更新投资补助。1.鼓励主导产业发展。对新供地工业投资项目2023年度固定资产投资达3000万元（含）以上且设备投资额在300万元（含）以上的，按设备投资额的8％给予补助；固定资产投资达5000万元（含）以上且设备投资额在500万元（含）以上的，按设备投资额的10％给予补助。对投资强度达到生物科技产业园入园标准要求的租赁项目，2023年度设备投资额在300万元（含）以上的，按设备投资额的8％给予补助；设备投资额在500万元（含）以上的，按设备投资额的10％给予补助。2.鼓励企业加大投入。2023年度购置新机器设备投资额达到200万元（含）以上的工业企业，按机器设备投资额的10％给予补助。对成功实现全流水线自动化改造的，按照实际投资额的30％给予补助。以上两项需上规且研发投入占营业收入比重达到3.5％以上享受全额奖励，未达到要求的按70％执行，单个项目最高补助500万元。3.鼓励企业数字化改造。制造业企业在核心业务环节投资应用物联网、大数据、云计算等现代信息技术（服务），软件及关键核心硬件投资额超过30万元（含）的，按实际发生费用的30％给予一次性补助，单个企业最高补助100万元。三、数字化诊断采购费用。2023年数字化诊断采购中标金额29.7万元，根据《嘉善县-庆元县制造业高质量发展结对促共富合作协议书》约定，其中27万元向嘉善县经信局专项申请，剩余2.7万元从本专项资金中列支。',0,'https://mapi.zjzwfw.gov.cn/web/mgop/gov-open/zj/2002319217/reserved/index.html#/policy-list/detail?esId=1-55992',NULL,'13056827016','丽水市庆元县经济商务局投资与信息化科',0,0,1,NULL,'经济信息和商务合作局',NULL,'庆元县域内企业',NULL,0,NULL,'','',NULL);
