insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (643,'2024年工业经济稳进提质（南湖经济信息和商务合作局）','2024-01-29','2099-12-31','一、加速企业倍增发展。对 2024 年一季度工业产值首 次达到 2.5 亿元、12.5 亿元、25 亿元的制造业企业，分别 给予 25 万元、100 万元、200 万元奖励； 三、支持新设企业快速释放产能。对 2023 年 1 月 1 日以 来月度新入规且 2024 年一季度工业产值实现增长的制造业企业予以奖励。其中，一季度比去年同期产值增加额达到 10 亿 元的，给予 150 万元奖励；比去年同期产值增加额达到 1 亿元 的，给予 50 万元奖励；比去年同期产值增加额达到 1000 万元 的，给予 5 万元奖励。 四、支持中小微企业加速成长。对于 2024 年一季度月度 新入规的制造业企业，给予一次性 20 万元奖励。对于 2023 年 1 月 1 日以来“小升规”制造业企业，2024 年一季度产值 增长 15%及以上的，给予 5 万元补助。对规下工业样本企业， 2024 年一季度应税销售收入同比增长 20%（含）以上的，给予 2 万元补助',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-03-29/1711700758907.pdf',NULL,'82838579','',0,0,0,NULL,'经济和信息化局',NULL,'南湖区注册合规企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (644,'2024年一季度工业经济稳增长第二批奖励（南湖经济信息和商务合作局）','2024-01-30','2099-12-31','支持企业有效扩大产能。对2023年1月1日前已是规模以上企业、且2024年一季度工业产值同比增长10%及以上、且2024年4-5月工业产值应同比增长10%及以上的规上制造业企业实行增长奖励。对于一季度产值同比增长10%以上部分且该部分增加额超过200万元的，市区联动按照增加额的1.5%予以奖励，单家企业最高奖励不超过600万元。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-09-18/1726622417584.doc',NULL,'82838579','',0,0,0,NULL,'制造业高质量发展作领导小组办公室',NULL,'南湖区注册合规企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (645,'2024年一季度工业经济稳增长第一批奖励（南湖经济信息和商务合作局）','2024-01-30','2099-12-31','一、加速企业倍增发展。对2024年一季度工业产值首次达到2.5亿元、12.5亿元、25亿元的制造业企业，市区联动分别给予25万元、100万元、200万元奖励。二、支持企业有效扩大产能。对2023年1月1日前已是规模以上企业、且2024年一季度工业产值同比增长10%及以上、且2024年4-5月工业产值应同比增长10%及以上的规上制造业企业实行增长奖励。对于一季度产值同比增长10%以上部分且该部分增加额超过200万元的，市区联动按照增加额的1.5%予以奖励，单家企业最高奖励不超过600万元。三、支持新设企业快速释放产能。对2023年1月1日以来月度新入规且2024年一季度工业产值实现增长的制造业企业予以奖励。其中，一季度比去年同期产值增加额达到10亿元的，市区联动给予一次性150万元奖励；比去年同期产值增加额达到1亿元的，市区联动给予一次性50万元奖励；比去年同期产值增加额达到1000万元的，市区联动给予一次性5万元奖励。四、支持中小微企业加速成长。对于2024年一季度月度新入规的制造业企业，市区联动给予一次性20万元奖励。对于2023年1月1日以来“小升规”制造业企业，2024年一季度产值增长15%及以上的，市区联动给予一次性5万元补助。对规下工业样本企业，2024年一季度应税销售收入同比增长20%（含）以上的，市区联动给予一次性2万元补助。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-09-18/1726622417584.doc',NULL,'82838579','',0,0,0,NULL,'制造业高质量发展作领导小组办公室',NULL,'南湖区注册合规企业',NULL,0,NULL,'','',NULL);
