insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (659,'2024年度第八批嘉兴市级工业和信息化发展资金项目','2024-04-23','2099-12-31','对经认定的国家级、省级、市级服务型制造示范企业（平台、项目），分别给予150万元、50万元、20万元奖励。对经认定的国家级、省级、市级工业设计企业（中心、研究院），分别给予150万元、50万元、20万元奖励；对获得中国优秀工业设计奖、中国设计智造奖“金智奖”、iF产品设计金奖、Reddot产品设计至尊奖等的企业，给予50万元奖励，同一产品不重复奖励。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-09-25/1727253978660.doc',NULL,'82838580','',0,0,0,NULL,'经济和信息化局',NULL,'南湖区注册合规企业',NULL,0,NULL,'（1）智能化技术改造项目。按照关于印发《嘉兴市级工信资金投资类项目操作办法》的通知（嘉经信投资〔2024〕14号）文件要求执行，其中文件规定的智能化技术改造项目储备条件中第5点条件不作要求。（2）绿色化技术改造项目。①项目已备案（或核准），符合国家产业政策、节能政策、环保政策；项目建设已进入试运行阶段或设备进场联调阶段，光伏项目已并网发电。②项目设备投资额符合补助条件。以会计师事务所出具的审计报告为准，包括项目改造设备购置和改造过程中信息技术应用和集成创新投入的不含税金额，原则上应发生在项目备案（核准）或取得项目代码日（含）之后，且最后一张投资发票在项目申报截止日（含）前一年以内。③项目年节能（减碳）或节水量符合补助条件。以有资质的第三方检测机构出具的评价报告为准。','',NULL);
