insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (674,'2024年市领军人才购房补助市级补助部分','2024-10-12','2099-12-31','根据《嘉兴市重大人才工程入选者购房租房补贴管理办法》（嘉人社〔2019〕92号）等文件规定，补助购房补贴到个人，并要求其按规定用途使用，提高人才资金使用绩效。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-10-29/1730164675596.png',NULL,'82061860','',0,0,0,NULL,'人力资源和社会保障局',NULL,'南湖区合规企业个人',NULL,0,NULL,'对2016年及以前在嘉兴申报入选（含入选国家、省“千人计划”人才后直接纳入“创新嘉兴•精英引领计划”领军人才）的国家“千人计划”人才（含中组部划转认定）、国家“万人计划”人才、浙江省“万人计划”杰出人才、浙江省特级专家、“创新嘉兴•精英引领计划”杰出人才团队负责人、“创新嘉兴•精英引领计划”领军人才等重大人才工程的，给予购房补贴30万元。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (675,'2024年市领军人才项目市级补助资金','2024-10-12','2099-12-31','根据《“星耀南湖”领军人才计划实施办法》（嘉人社〔2022〕64 号）、《“星耀南湖”领军人才计划资金拨付管理暂行办法》（嘉委人才办〔2023〕13号）、《关于下达2024年市级人才专项资金任务清单（第三批）的通知》（嘉人社〔2024〕38号）等文件规定，补助人才专项补助资金拨付到各相关单位，并要求其按规定用途使用，提高人才资金使用绩效。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-10-29/1730164675596.png',NULL,'82061860','',0,0,0,NULL,'人力资源和社会保障局',NULL,'南湖区合规企业个人',NULL,0,NULL,'本次补助对象为入选嘉兴市领军人才的项目，经实地走访、审计，确认资金补贴金额。其中，（一）创业人才项目。创业团队项目按A+类1000万元、A类800万元、B类500万元、C类300万元、D类200万元给予项目资助。项目资助分为创业启动资助、创业考核资助和创业奖励资助（仅针对A和A+类项目）。创业领军人才签订三方落户协议，符合条件后，根据企业启动运营评估情况拨付创业启动资助，经绩效评估确认实现协议约定相关产业化预期目标后拨付第二笔创业考核资助。A和A+类项目先按照500万元标准分两笔发放，经绩效评估确认达成协议约定相关产业化预期目标后，可在拨付创业考核资金后拨付剩余资金作为创业奖励资助。（二）创新人才项目。创新人才项目按年度补助。对入选人才到岗后与用人单位签订聘用合同，并通过拨付评估后，按人才年薪的A类70%、B类60%、C类50%给予用人单位引才补助。青年创新人才项目每年最高给予A类80万元、B类50万元、C类30万元补助），补助时间不超过3年。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (676,'2024年度南湖区稻渔综合种养补助资金','2024-10-23','2099-12-31','根据《南湖区稻渔综合种养补助实施办法》要求 ，经委托第三方测绘和核查，2024年度全区稻渔综合种养总面积926.47亩，补助标准600元/亩（市、区财政各承担50%），此次拟拨付市、区财政补助资金合计55.5882万元。其中下达南湖区2024年度稻渔综合种养补助资金（个人）33720元，由330402240130039001014-市农业专项-农业农村发展新支出16860元，由330402220122039001040-农渔技术示范推广专项支出16860元。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-11-12/1731376074369.pdf',NULL,'0573-82838434','',0,0,0,NULL,'农业农村局（厅）',NULL,'南湖区注册合规企业个人',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (677,'关于组织2024年度第二批浙江省小微企业园申报认定的通知','2024-11-12','2024-11-22','为充分发挥小微企业园培育支持小型、微型企业平稳发展的作用，增强其承载和服务小微企业的功能，加快建设升级版小微企业园，促进我市小微企业高质量发展，根据《中共浙江省委办公厅 浙江省人民政府办公厅关于加快小微企业园高质量发展的实施意见》（浙委办发〔2018〕59号）、《中共宁波市委办公厅 宁波市人民政府办公厅关于加快小微企业园高质量发展的实施意见》（甬党办〔2019〕6号）、《中共宁波市委 宁波市人民政府关于印发全市组织开展危险化学品使用单位及小企业小加工作坊安全生产和消防安全整治百日大会战工作方案的通知》（甬党发〔2019〕64号）要求，结合《浙江省小微企业园绩效评价试行办法》（浙中小企业办〔2018〕18号）精神，制定本办法。',0,'http://oa4.bl.gov.cn/bljjhxxhj/jwoa4app/db_archive.nsf/all/9C88',NULL,'89384559','刘老师',0,0,2,NULL,'经济和信息化局',NULL,'小微企业',NULL,0,NULL,'小微企业园应具备以下基本条件：（1）规划建设上，符合区县（市）小微企业园建设规划，统一开发建设或改造提升，有必要的公共配套设施和一定比例的生产生活服务设施；（2）运营管理上，有明确的运营管理机构和运营团队，承担园区统一管理和提供公共服务。运营机构具有独立的法人资格，治理结构规范；运营团队具有较高的管理水平和较丰富的创新创业服务经验，具备整合各类社会化服务资源的能力；园区具有切实可行的运营制度、明确的发展规划、年度目标和实施方案；（3）安全管理上，有严格的安全工作标准制度和年度工作计划，有专业的安全管理人员，园区安全生产活动经常化、制度化，有完善的安全设施，较高的园区建筑设防等级，安全设备能正常使用；（4）建设规模上，生产制造类园区占地面积20亩以上或建筑面积2万平方米以上，生产性服务类等其他园区建筑面积一般在1万平方米以上；（5）产业集聚上，有明确的企业入园条件，主导产业特色明显，数量原则上不超过2个。小微企业数量占比70%以上，主导产业集聚度达到50%以上，生产制造类园区入驻企业不少于10家，生产性服务类等园区入驻企业不少于20家。','按要求需进行绩效评价的小微企业园，填写《宁波市小微企业园绩效评价评分表》，并提交至各区县（市）经信部门。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (678,'关于组织申报2024年度市级数字化车间/智能工厂项目的通知','2024-11-04','2024-11-19','为深入贯彻市委、市政府打造全球智造创新之都的战略部署，加快发展新质生产力，引领带动企业数字化转型升级发展，根据《宁波打造全球智造创新之都行动纲要（2022—2026年）》（甬党办〔2022〕69号）文件精神和年度工作计划，现决定开展2024年度市级数字化车间/智能工厂项目申报工作。',0,'关于组织申报2024年度市级数字化车间/智能工厂项目的通知',NULL,'89384559','刘老师',0,0,0,NULL,'经济和信息化局',NULL,'制造业企业',NULL,0,NULL,'（一）申报企业应为依法经营，信用状况良好的工业企业，具有部署实施项目的财务投资能力，并有健全的管理团队和人才队伍。（二）申报项目应具有技术先进、市场潜力大、示范带动作用强的特征，有较强的行业代表性，愿意积极配合参加政府组织的经验推广、现场交流、参观学习等活动，发挥示范带头作用。（三）项目要求于2023年1月（含）以后开工建设，且于2024年12月底前竣工。（四）项目应按规定完成项目备案（核准），且列入市级数字化车间/智能工厂项目计划库（入库网址：https://qyfw.jxj.ningbo.gov.cn/#/index/policyLibrary?id=7）。还未入库的项目，请在申报截止时间前，及时将项目填报入库。（四）项目总投资（不含土地和建筑费用，限于外购硬件设备、外购工业软件和技术服务）原则上在1000万元以上。所有投入额均为不含税金额。（五）项目建设要求请参照《宁波市数字经济与制造业深度融合建设评价体系指引（试行）》中“数字化车间/智能工厂项目评价指标”。（六）已认定为市级数字化车间/智能工厂项目的不再重复申报。上两年度已获评市级数字化车间/智能工厂项目的企业，不得再次申报。','符合条件的企业登录宁波企业综合服务平台大平台（https://qyfw.jxj.ningbo.gov.cn/）进行网上填报，上传对应的申报材料（附件1）。其中，申报材料中真实性声明需要盖章上传。纸质材料（一式2份，与网上材料一致）提交至属地经信部门。企业申报材料上报截止时间为11月19日，逾期不予以受理。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (679,'关于开展2024年度省级工业设计中心认定的通知','2024-10-28','2024-11-22','2024年度省级工业设计中心认定工作已启动，申报主体为已设立工业设计中心的企业、工业设计企业两类。请符合条件的企业积极申报，具体内容及要求请见文件。\n\n请各申报主体于11月22日前完成网上申报，同时将纸质材料一式一份盖章后交北仑经信局。',0,'宁波市甬易办平台',NULL,'89384564','李老师',0,0,1,NULL,'经济和信息化局',NULL,'制造业企业',NULL,0,NULL,'申报省级工业设计中心，需稳定运营3年及以上，有固定的工作场所、良好的软硬件条件、健全的管理制度、稳定的人员配置，并具备以下条件：1.遵守国家法律法规，3年内未发生重大环保、质量和安全事故，未被列为严重失信主体，没有重大违法行为或涉嫌重大违法正在接受有关部门审查的情况；2.制造业企业等单位设立的企业工业设计中心须是专门成立、独立运行的分支机构或内设部门；3.原则上从市经信局认定的市级工业设计中心中择优确定。','一、企业工业设计中心申报材料清单\n（一）《省级工业设计中心申请表》（企业工业设计中心）；\n（二）*企业工业设计中心近三年专项审计报告（含企业生产经营主要数据，工业设计中心近三年运营、设计费用投入、设计费用投入占企业研发设计投入总额的比例、专利、专业从业人员等主要情况）；\n（三）企业设立独立工业设计中心相关佐证材料；\n（四）设计团队人员名单（包含学历、工业设计专业技术职称等佐证材料）；\n（五）工业设计成果获奖证书复印件；\n（六）工业设计成果获得专利、版权等清单（含产品或项目名称、专利名称、专利号、权利人、授权单位、授权时间等）；\n（七）牵头或参与设计标准制定情况及佐证材料；\n（八）重要工业设计项目及主要成果产业化佐证材料；\n（九）其他有关材料。\n二、工业设计企业申报材料清单\n（一）《省级工业设计中心申报表》（工业设计企业）；\n（二）*工业设计企业近三年专项审计报告（含企业经营主要数据，包含营收、工业设计服务营收及占企业总收入比例、净利润、利税、资产负债、现金流、投入、专利、专业从业人员等主要情况）；\n（三）设计团队人员名单（包含学历、工业设计专业技术职称等佐证材料）；\n（四）工业设计成果获奖证书复印件；\n（五）工业设计成果获得专利、版权等清单（含产品或项目名称、专利名称、专利号、权利人、授权单位、授权时间等）；\n（六）完成的工业设计项目清单及主要工业设计成果产业化佐证材料；\n（七）企业制度建设情况（包括管理制度、知识产权保护制度、发展规划等）；\n（八）其他有关材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (680,'关于公布北仑区绿色化改造完成企业名单的通知','2024-08-20','2099-12-31','根据《宁波市星级绿色工厂评价办法》（甬经信绿制〔2024〕60号），绿色化综合评分60分以上企业，历年三星级及以上绿色工厂，市级及以上节水型企业，通过清洁生产审核或节能技改验收企业可直接认定为绿色化改造完成企业，现对万华化学（宁波）有限公司等429家绿色化改造完成企业名单予以公布。具体见附件。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (681,'关于公布北仑区制造业企业数字化等级评价结果（第一批）的通知','2024-06-26','2099-12-31','根据《宁波市制造业企业数字化等级评价办法（试行）》（甬经信产数〔2024〕24号）《关于开展宁波市制造业企业数字化等级（第一批）认定申报工作的通知》（甬经信产数〔2024〕23号）要求，经企业申报（或直接评定）、专家评审和社会公示，确定我区129家企业数字化等级评价结果（第一批），现予以公布（详见附件）。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (682,'关于组织开展2024年度宁波市制造业企业数字化等级认定申报工作的通知','2024-06-26','2099-12-31','为推动数字技术与实体经济深度融合，加快企业“智改数转”全覆盖，根据《宁波市制造业企业数字化等级评价办法（试行）》（甬经信产数〔2024〕24号）、《关于组织开展2024年度宁波市制造业企业数字化等级认定申报工作的通知》（甬经信产数〔2024〕81号）等文件要求，现就开展宁波市制造业企业数字化等级认定申报有关工作事项通知如下。',0,'宁波市甬易办平台',NULL,'89384559','刘家鹏',0,0,0,NULL,'经济和信息化局',NULL,'制造业企业',NULL,0,NULL,'（一）申请主体具有独立法人资格的制造业企业，且财务管理制度健全，企业信用状态良好。（二）申请主体重视智能制造工作，具有一定的数字化管理和实施能力。（三）申请主体通过应用数字化技术、智能装备、工业软件等，带动企业的数字化水平提升，并取得一定的绩效。','企业自主申报。符合条件的企业登录宁波市中小企业公共服务平台（网址：https://qyfw.jxj.ningbo.gov.cn）中的宁波市制造业企业数字化等级评价平台进行网上填报（企业操作手册见附件１），根据要求上传对应的申报资料及佐证材料（格式见附件２），并对申报材料中真实性声明进行盖章上传。数字化等级评价平台长期开放，企业可根据自身情况开展申报工作。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (683,'关于2023年度北仑区现代农业领域科技示范项目的公示','2024-06-25','2099-12-31','2023年度北仑区现代农业领域科技示范项目经各有关单位推荐申报，形式审查、行业专家评审和区科技局党组会议讨论，拟认定进口朱顶红的引种及筛选并开发利用等5个项目为2023年度北仑区现代农业领域科技示范项目。现将拟认定结果进行公示，具体名单请见附件。\n\n任何单位和个人对公示的拟奖项目持有异议的，请以书面形式写明异议的事实依据、本人真实姓名及联系方式，并在2024年7月2日前送交宁波市北仑区科学技术局。我局将按照有关规定对异议项目进行核实、查证，对持异议者身份予以保密。对匿名异议不予受理。',0,'宁波市甬易办平台',NULL,'89386826','北仑区科学技术局科技合作科',0,0,0,NULL,'科学技术局（厅）',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (684,'关于认定2024年度第一批宁波市北仑区（开发区）企业工程技术中心名单的通知','2024-06-05','2099-12-31','根据《宁波市北仑区（开发区）企业工程技术中心认定管理办法（2021年修订）》(仑科〔2021〕14号)文件要求，经企业自愿申报和归口管理部门推荐，区科技局会同区发改局、经信局和财政局（厅）进行联审，现认定宁波市北仑区（开发区）浙江乘鹰高性能胶黏剂工程技术中心等35家企业工程技术中心（具体名单见附件）为2024年度第一批宁波市北仑区（开发区）企业工程技术中心。\n\n望上述35家企业工程技术中心进一步建立健全规章制度，加快中心的团队建设和人才培养，积极开展技术创新活动，为科技成果转化发挥积极的作用。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'科学技术局（厅）',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (685,'《北仑区旅游产业扶持政策 实施细则》的通知','2024-05-06','2024-12-31','根据《北仑区关于进一步扩市场增动能强活力推动经济高质量发展的若干政策》（仑政〔2024〕13号文件）文件精神，为推动我区旅游产业高质量发展，结合我区实际，特制定本细则。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'文化广电旅游体育局',NULL,'住宿和餐饮业/文化、体育和娱乐业/居民服务、修理和其他服务业',NULL,0,NULL,'扶持资金主要扶持对象是在我区行政区域内依法登记注册且纳入文旅主管部门行业管理的法人、非法人组织和城乡居民。本细则第七条“2.旅游市场营销推广补助”所涉及企业不受此条区域限制。企业单位和社会组织应具备的基本条件：1.合法经营，依法纳税，管理规范；2.具有健全的财务管理制度和良好的财务记录；3.按本细则规定向区文旅部门报送有关资料。','区文广旅体局根据各项工作完成情况印发申报通知，具体申报条件及申报材料要求详见当年申报通知。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (686,'关于发布2023年度北仑区工业企业“亩均效益”综合评价分类结果的公告','2024-04-19','2025-04-01','根据《北仑区工业企业“亩均效益”综合评价办法（2024年修订版）》（仑亩均办〔2023〕7号）文件，我办已经完成2023年度全区规上工业企业，规下工业企业及其他拥有工业土地使用权经营主体的亩均评价工作，评价结果已正式印发，现将评价结果予以公告，具体名单详见附件。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (687,'关于印发《北仑区科技人才经纪人团队奖励资金管理办法》的通知','2024-04-12','2099-12-31','为加强我区人才服务支持和经纪人团队建设，助力企业提升研发投入管理水平，激发区域科技创新活力，根据《关于加强和改进新时代人才工作加快打造一流人才发展首选地的若干意见（试行）》（仑政〔2023〕83号），现制定此办法。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'科学技术局（厅）',NULL,'不限',NULL,0,NULL,'（一）规上企业内部组建科技经纪人团队的，按下述条件给予奖励，具体标准为：1、助力企业首次建立研发投入核算制度、研发经费有效“清零”的科技经纪人团队，按以下标准给予奖励：（1）企业年度研发经费内部支出达到50万元的，给予一次性最高3000元奖励；（2）企业年度研发经费内部支出达到300万元的，给予一次性最高6000元奖励；（3）企业年度研发经费内部支出达到500万元的，给予一次性最高10000元奖励。2、助力已建立研发投入核算制度的企业，其研发经费同比增长达一定比例的科技经纪人团队，按以下标准给予奖励：（1）企业年度研发经费内部支出在500万元以上且增幅在30%以上的，给予一次性最高5000元奖励；（2）企业年度研发经费内部支出在2000万元以上且增幅在20%以上的，给予一次性最高8000元奖励；（3）企业年度研发经费内部支出在1亿元以上且增幅在10%以上，给予一次性最高10000元奖励。（二）服务机构内部组建科技经纪人团队，积极配合科技部门开展优质、高效服务的，按以下标准给予奖励：1、开展专项辅导培训超20场次（含），规上企业研发经费有效“清零”超10家（含），给予一次性最高1万元奖励；2、开展专项辅导培训超30场次（含），规上企业研发经费有效“清零”超20家（含），给予一次性最高2万元奖励；3、开展专项辅导培训超40场次（含），规上企业研发经费有效“清零”超30家（含），给予一次性最高3万元奖励。','1、各企业填报申报表，规上企业研发经费数据以科技统计年报数据为准。省级以上重点人才项目申报数量以相关业务主管部门核定为准。\n\n2、各服务机构填报申报表，并上报有效“清零”依据和培训服务台帐等佐证。\n\n3、各科创平台填报申请表，并上报相关工作材料。省级以上重点人才项目申报数量以相关业务主管部门核定为准。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (688,'北仑区全面实施标准化战略领导小组办公室关于印发2024年北仑区标准化创新发展暨城市标准化综合试点主要工作任务的通知','2024-04-08','2099-12-31','为贯彻《国家标准化发展纲要》精神，扎实推进北仑区标准化创新发展暨城市标准化综合试点，根据《浙江省人民政府办公厅关于印发浙江省国家标准化创新发展试点工作方案的通知》(浙政办发〔2023〕24号)、《浙江省市场监督管理局关于组织开展标准化创新发展试点的通知》（浙市监标准〔2023〕3号）、《宁波市北仑区府办关于印发宁波市北仑区标准化创新发展城市标准化综合试点方案的通知》(仑政办〔2023〕47号)等有关文件要求，结合2023年试点任务完成情况，特制定2024年北仑区标准化创新发展暨城市标准化综合试点主要工作任务。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'市场监督管理局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (689,'关于印发《北仑区关键核心技术攻关项目实施细则（修订）》的通知','2024-04-08','2099-12-31','为贯彻落实《北仑区关键核心技术攻关项目实施方案（修订）》（仑科〔2024〕2号）文件精神，规范关键核心技术攻关项目管理，现将《北仑区关键核心技术攻关项目实施细则（修订）》印发给你们，请认真贯彻执行。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'科学技术局（厅）',NULL,'在北仑区行政区域内设立、登记、注册并具有独立法人资格的企业',NULL,0,NULL,'一、攻关项目承担单位应为在北仑区行政区域内设立、登记、注册并具有独立法人资格的企业，鼓励与产业链上下游企业、高校、科研院所等协同申报。二、攻关项目设领军类、强链类和育成类三个类别。领军类项目：上年度企业研发经费内部支出高于5000万元，且符合以下条件之一：（1）研发经费内部支出占营业收入比重不低于3%；（2）有效期内的高新技术企业。强链类项目：上年度企业研发经费内部支出高于1000万元，且符合以下条件之一：（1）研发经费内部支出占营业收入比重不低于4%；（2）有效期内的高新技术企业。育成类项目：符合以下条件之一：（1）上年度企业研发经费内部支出高于200万元；（2）研发经费内部支出占营业收入比重不低于5%；（3）有效期内的高新技术企业。三、攻关项目申报单位需具备完成项目所必需的研发基础，有健全的科研管理、财务管理和知识产权管理等制度，企业信誉良好。四、攻关项目申报单位、合作单位以及项目团队成员诚信状况良好，在区级及以上财政专项资金审计、检查过程中无重大违规行为，无在惩戒执行期内的科研严重失信行为记录和相关社会领域信用“黑名单”记录。五、攻关项目负责人在相关技术领域有较高的学术地位或技术优势，有完成项目所需要的组织管理协调能力。','攻关项目实行牵头单位申报制。区科技局每年征集项目技术需求，发布项目申报指南，项目牵头单位根据指南要求，如实填写《北仑区关键核心技术攻关项目申报书》、《北仑区关键核心技术攻关项目可行性报告》等申报材料。申报材料要实事求是，突出重点。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (690,'关于印发《北仑区关键核心技术攻关项目实施方案（修订）》的通知','2024-03-27','2099-12-31','为深入贯彻落实党的二十大关于加快实施创新驱动发展战略的总体部署，助力国家高水平推进科技自立自强，加快实现北仑区重点领域、关键技术的重大突破，根据《北仑区（开发区）促进产业高质量发展扶持办法》（仑政〔2022〕24号）和《关于加强和改进新时代人才工作加快打造一流人才发展首选地的若干意见（试行）》（仑政〔2023〕83号）等文件精神，实施“强港强区”关键核心技术重点研发计划，现对《北仑区关键核心技术攻关项目实施方案》进行修订。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'科学技术局（厅）',NULL,'在北仑区行政区域内设立、登记、注册并具有独立法人资格的企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (691,'关于组织申报2024年度北仑区博士创新站的通知','2024-03-25','2099-12-31','为进一步推进北仑区博士等高层次人才的引进和服务，加快构建以企事业单位为主体、市场为导向、产学研深度融合的技术创新体系，根据《北仑区博士创新站建设管理办法》（仑科协〔2023〕24号）文件要求，经研究，决定开展2024年度北仑区博士创新站申报工作。',0,'宁波市甬易办平台',NULL,'0574-89383439','牛鸣川、胡丽娜',0,0,0,NULL,'科学技术局（厅）',NULL,'不限',NULL,0,NULL,'博士创新站的建设主体原则上为区内企事业单位、高等院校、科研院所、民办非企业等。申请建设博士创新站应具备以下条件：1.建站单位必须具备独立法人资格，在北仑区内注册，生产运营状况良好，有明确的技术创新需求和合作项目，能为博士及其创新团队提供必要的科研工作条件及生活保障；2.建站博士必须是已获得国家教育部认可的博士学位，与建站单位应无人事隶属关系，具有扎实的相关产业（行业）领域专业知识和较强的研发能力、组织协调能力以及工作责任心。每名博士领衔的博士创新站原则上不超过1家；3.建站单位至少与1名符合建站条件的博士签订协议，开展实质性的项目合作，合作内容符合当地产业发展方向和重点。','1.《北仑区博士创新站申请表》（见附件）；\n\n2.单位及建站博士简介；\n\n3.合作项目介绍；\n\n4.相关证明材料（单位法人证书复印件、博士学位证书复印件、单位与博士签订建站协议、合作项目协议等）；\n\n5.其他附件（包括荣誉证书、资质证书等复印件）。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (692,'关于组织申报2024年度北仑区院士工作站、院士科技创新中心的通知','2024-03-25','2099-12-31','为深入贯彻中办《关于进一步弘扬科学家精神加强作风和学风建设的意见》（以下简称《意见》）等文件精神，进一步推进北仑区顶尖人才的引进和服务，促进高层次产学研合作，充分发挥院士等顶尖人才及其团队对北仑自主创新的支撑和带动作用，助力我区“双一流双示范”建设，根据《北仑区院士工作站、院士科技创新中心建设管理办法》（仑科协〔2023〕23号）文件要求，经研究，决定开展2024年度北仑区院士工作站、院士科技创新中心申报工作。',0,'宁波市甬易办平台',NULL,'0574 - 89383439','牛鸣川、胡丽娜',0,0,0,NULL,'科学技术局（厅）',NULL,'不限',NULL,0,NULL,'工作站（科创中心）的建设单位主要是高新技术企业或行业骨干企业，有条件的科研院所、高等院校、医院、科技园区等单位也可建立工作站（科创中心）。申请建设工作站（科创中心）单位应具备的条件：   1.在北仑区注册，具有独立法人资格，生产经营或事业发展状况良好，能为院士及其团队提供必要的科研和生活条件；    2.建立工作站的，需与院士签订协议，并与院士及其团队有实质性的项目合作；建立科创中心的，需与院士签订协议或提供院士愿意建立科创中心的证明材料，并与院士及其团队有实质性的项目合作；   3.与院士及其团队合作方向应符合北仑产业政策导向，能够引领高新技术产业发展或促进传统产业转型升级；4.有较强研发能力，配有水平较高、结构合理的研发团队及相应配套研发设施；   5.有专人联系对接工作站（科创中心）建设，有固定工作场所和经费支持，能够保障院士及其团队进站（中心）工作，已形成较完善的管理制度和服务体系；6.符合下列条件之一的单位可优先推荐申报工作站（科创中心）：（1）与院士及其团队已建立长期合作关系；（2）建有市级以上工程（技术）研究中心、重点（工程）实验室、博士后科研工作站等创新平台；（3）承担国家或省、市级重点科技创新项目。','（一）院士工作站申报材料\n\n1.北仑区院士工作站建设申请表（附件1、附件2）；\n\n2.单位简介；\n\n3.院士工作站建站目的；\n\n4.与院士合作项目介绍；\n\n5.申报单位院士工作站管理办法；\n\n6.相关证明材料（单位法人证书复印件、单位与院士共建工作站协议、单位与院士或院士团队的项目合作协议（技术开发合同）、建站院士为外籍院士或海外院士的请提供院士证书和翻译机构盖章的翻译件等证明材料）；\n\n7.其他附件（包括荣誉证书、资质证书等复印件）。\n\n（二）院士科技创新中心申报材料\n\n1.北仑区院士科技创新中心建设申请表（附件3、附件4）；\n\n2.单位简介；\n\n3.院士科技创新中心建设目的；\n\n4.与院士合作项目介绍；\n\n5.申报单位院士科技创新中心管理办法；\n\n6.相关证明材料（单位法人证书复印件、单位与院士共建院士科技创新中心协议或院士愿意建立科创中心的证明材料、单位与院士或院士团队的项目合作协议（技术开发合同）、建中心院士为外籍院士或海外院士的请提供院士证书和翻译机构盖章的翻译件等证明材料）；\n\n7.其他附件（包括荣誉证书、资质证书等复印件）。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (693,'北仑区经济和信息化局关于公布2024年度第一批创新型中小企业名单的公告','2024-02-18','2099-12-31','根据《关于组织做好第三批”小巨人“企业复核准备及开展2024年第一批宁波市创新型中小企业评价的通知》(甬经信中小〔2023〕173号)，经企业申报，我局组织评审，现认定宁波舒仰包装纸有限公司等105家企业为创新型中小企业，具体企业名单见附件。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (694,'宁波市北仑区市场监督管理局 关于印发2024年度北仑区第一批区级地方技术性规范制订项目计划的通知','2024-01-02','2099-12-31','现将《2024年度北仑区第一批区级地方技术性规范制订项目计划》印发给你们，请按照《浙江省地方标准管理办法》等程序要求，认真组织实施，广泛听取意见，确保标准质量和水平，按计划及时完成标准制订工作。',0,'宁波市甬易办平台',NULL,'123568954','',0,0,0,NULL,'市场监督管理局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (695,'玉环市科学技术局转发《台州市科学技术局关于加快促进高新技术产业园区高质量发展的实施意见》的通知','2024-10-30','2099-12-21','为贯彻落实《国务院关于促进国家高新技术产业开发区高质量发展的若干意见》《浙江省人民政府关于加快促进高新技术产业开发区（园区）高质量发展的实施意见》《台州市推进产业平台高质量发展实施意见》，促进全市高新技术产业园区（以下简称高新区）高质量发展，现提出如下实施意见。',0,'https://www.yuhuan.gov.cn/art/2024/10/30/art_1229305458_1714862.html',NULL,'123568954','',0,0,0,NULL,'科学技术局（厅）',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (696,'玉环市核心关键技术攻关计划项目管理办法（试行）','2024-02-22','2099-12-21','为规范和加强玉环市核心关键技术攻关计划项目管理，完善相关运行机制，规范管理程序，根据《浙江省重点研发计划暂行管理办法》（浙科发规〔2019〕1 10号）、《浙江省人民政府办公厅关于改革完善省财政科研经费管理的实施意见》（浙政办发〔2022〕22号）、《浙江省科技计划（专项、基金）项目验收管理办法》（浙科发计〔2017〕146号）、《浙江省科技发展专项资金管理办法》（浙财科教〔2023〕1号）等文件精神，结合玉环实际，制定本办法。',0,'https://www.yuhuan.gov.cn/art/2024/2/22/art_1229305458_1692034.html',NULL,'123568954','',0,0,0,NULL,'科学技术局（厅）',NULL,'企业、科研院所、高等院校及其他具备科研开发或科技服务能力的单位',NULL,0,NULL,'1.同一单位每批次申报项目数限1项，已承担市核心关键技术攻关计划项目未完成验收或结题的不得申请。2.申请单位具有较好的项目实施基础和保障条件。3.项目负责人原则上应为申报单位在职人员，且为该项目主体研究思路的提出者和实际主持研究的科技人员，如非在职人员，应由申请单位出具赋予其管理项目实施的授权书。项目负责人原则上不得调离或辞去工作职位。4.同一科研人员作为项目负责人，承担在研各类市级科技计划项目不超过2项。5．项目组成员结构基本合理。','1．项目申请书。\n\n2．项目可行性报告。\n\n3．多个单位合作实施的由一个牵头单位提出申请，并附正式合作合同或合作协议。\n\n4．诚信承诺书。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (697,'进一步完善玉环市政府产业基金运作管理的若干意见（试行）','2024-11-04','2099-12-21','为进一步细化落实《玉环市人民政府关于印发玉环市产业基金管理办法的通知》（玉政发〔2022〕34号）、《玉环市人民政府办公室关于玉环市产业子基金管理暂行办法的通知》（玉政办发〔2022〕53号），规范开展玉环市政府产业基金的设立、运作、管理等工作，提升基金的使用绩效，根据《财政部关于加强政府投资基金管理提高财政出资效益的通知》（财预〔2020〕7号）、《浙江省财政厅关于进一步加强政府产业基金投资运作管理的指导意见》（浙财金〔2021〕42号）等文件规定，结合玉环实际，现就进一步完善政府产业基金运作管理提出如下若干意见。',0,'https://www.yuhuan.gov.cn/art/2024/11/4/art_1229303236_1714953.html',NULL,'123568954','',0,0,0,NULL,'政府办公室',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (698,'玉环市推进新型城镇化建设实施意见','2024-10-23','2099-12-21','为深入贯彻落实《台州市推进新型城镇化建设实施意见》（台政办发〔2024〕21号），全面提升城市品质形象，显著增强城市综合承载能力，加快构建城乡融合发展新格局，结合玉环实际，特制定本实施意见。',0,'https://www.yuhuan.gov.cn/art/2024/10/23/art_1229303236_1713914.html',NULL,'123568954','',0,0,0,NULL,'政府办公室',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (699,'玉环市政府投资项目工程预结算管理办法（试行）','2024-05-17','2099-12-21','为提高我市政府投资项目工程预结算编审工作的质量和效率，强化项目业主的主体责任，加强对中介机构编审质量的监督管理，提高财政资金使用效益，根据《中华人民共和国预算法》、《财政投资评审管理规定》（财建〔2009〕648号）、《浙江省政府投资项目管理办法》（省政府令第363号）、《浙江省建设工程造价管理办法》（省政府令第378号）、《浙江省政府投资预算管理办法实施细则》（浙财建〔2019〕113号）、《台州市政府投资项目管理办法》（台政办发〔2018〕78号）等有关规定，结合我市实际，制定本办法。',0,'https://www.yuhuan.gov.cn/art/2024/5/17/art_1229560963_1693156.html',NULL,'123568954','',0,0,0,NULL,'政府办公室',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (700,'浙江省绿色（低碳）工厂梯度培育管理实施细则','2024-06-28','2099-12-21','为深入贯彻习近平生态文明思想，全面落实党的二十大关于推动绿色发展新要求，进一步完善浙江省绿色制造和服务体系建设，发挥绿色（低碳）工厂在制造业绿色低碳转型中的基础性和导向性作用，加快形成规范化、长效化培育机制，打造绿色制造领军力量，根据《“十四五”工业绿色发展规划》（工信部规〔2021〕178号）、《工业领域碳达峰实施方案》（工信部联节〔2022〕88号），以及《绿色工厂梯度培育及管理暂行办法》（工信部节〔2024〕13号）的要求，制定本实施细则。',0,'https://www.yuhuan.gov.cn/art/2024/6/28/art_1229303368_1695963.html',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'机械制造与重工业',NULL,0,NULL,'一、绿色（低碳）工厂培育对象应当符合下列条件：1.依法设立并具有独立法人资格或者独立核算的分支机构，且从事实际生产的制造型企业，注册地和实际生产场所在浙江省行政区域范围内；2.符合国家绿色工厂或浙江省绿色低碳工厂相关标准、政策要求。二、绿色（低碳）工业园区培育对象应当符合下列条件：1.具有法定边界和范围、具备统一管理机构的工业园区，且以产品制造和能源供给为主要功能，工业增加值占比超过50%；2.发布园区绿色（低碳）工厂培育计划，组织园区内企业开展绿色（低碳）工厂创建；3.符合国家绿色工业园区或浙江省绿色低碳工业园区相关标准、政策要求。三、绿色供应链管理企业培育对象应当符合下列条件：1.依法设立并具有独立法人资格或者独立核算的分支机构，注册地和实际生产（经营）场所在浙江省行政区域范围内，是行业影响力大、经营实力雄厚、产业链完整、绿色供应链管理基础好、在产业链发挥主导作用的企业，积极创建绿色（低碳）工厂；2.制定供应商绿色（低碳）工厂培育计划，推动供应商开展绿色工厂创建；3.符合国家绿色供应链管理企业或浙江省绿色低碳供应链管理企业相关标准、政策要求。','企业、园区可采取自评价或委托具备评价能力的第三方机构开展评价的方式，编写评价报告后通过管理平台提交。采取第三方评价方式的，第三方机构要按照工信部《绿色制造第三方评价工作要求》开展工作，对所出具评价报告的真实性和准确性负责。采取自评价方式的，工作流程和报告模板可参考工信部《绿色制造第三方评价工作要求》。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (701,'玉环市经信领域安全生产隐患大排查大整治行动方案','2024-01-22','2099-12-21','深入贯彻落实习近平总书记关于安全生产重要论述精神，统筹发展和安全，深刻吸取武义县伟嘉利工贸有限公司“4·17”重大火灾事故教训，聚焦经信领域安全生产、消防安全工作，压紧压实安全生产责任，推动安全生产治理模式向事前预防转变，提升本质安全水平，有效防范遏制较大社会负面影响和较大以上事故发生，为以“两个先行”打造“重要窗口”营造安全稳定环境。\n以重点领域为攻坚发力点，坚持排查全覆盖、整治全闭环，突出重大事故风险关键节点，深入开展问题隐患排查治理。通过安全生产隐患大排查大整治行动，深入查找安全生产工作短板和不足，举一反三实现本质提升，确保社会平安稳定。',0,'https://www.yuhuan.gov.cn/art/2024/1/22/art_1229303368_1691564.html',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'对橡塑、纸箱行业，无动力修造船舶和小微工业园区安全生产进行专项整治，同时推进小微工业园区“园中园”“厂中厂”整治任务',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (702,'关于组织申报2024年度台州市首台（套）装备认定工作的通知','2024-11-13','2024-11-26','为加快推进我市装备制造业发展，提高我市装备制造业自主创新能力和市场竞争力，根据《浙江省制造业首台（套）提升工程2024年工作要点》（浙制高办〔2024〕10号）、《关于组织2024年度台州市首台（套）装备认定工作的通知》（台经信装备〔2024〕184号）相关要求，现就开展2024年度台州市首台（套）装备认定工作有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/11/13/art_1229303414_4009045.html',NULL,'80751031','',0,0,21,NULL,'经济和信息化局',NULL,'装备制造业',NULL,0,NULL,'（一）申报单位应在台州市行政区域内依法设立，具有独立法人资格，管理规范，依法纳税，并在台州市境内从事研发、生产的企事业单位。申报装备应符合《台州市制造业高质量发展“十四五”规划》中装备制造业重点领域或市装备制造业年度工作重点等要求。（二）申报单位依法拥有申报装备的自主知识产权，或者通过依法受让取得知识产权。申报装备通过市级及以上质量技术监督部门资质认定的实验室或检验机构的检测。属于国家有特殊行业管理要求的产品，必须具有相关行业主管部门批准颁发的产品生产许可证；属于国家实施强制性产品认证的产品，必须通过强制性产品认证。医疗器械产品应同时提供产品技术要求和全性能检测报告。（三）申报装备应用新技术原理、新设计构思，在结构、材质、工艺等方面对原有产品率先进行根本性改进，产品的主要技术性能指标取得重大突破，在同类产品中应达到国内先进水平。须有两名及以上具有高级专业技术职务以上相关行业的专家出具的首台（套）推荐证明书。（四）申报单位应提供近三年重大工程应用或产业化应用等初步市场业绩证明，提供申报装备销售合同或销售发票。','申请表、申请报告书、单位的法人营业执照副本复印件、产品检测报告、查新报告、产品标准备案证明、2位专家推荐证明书、相关专利证明、产品销售合同或销售发票、企业年度财务审计报表、承诺书等。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (703,'关于组织申报2023年度工业企业购置工业机器人专项奖励资金的通知','2024-11-12','2099-12-21','为扎实推进企业智能化技术改造行动，鼓励企业扩大机器人投入，提升我市工业智能制造水平，根据《关于促进新时代美丽经济建设的若干意见（2023 年修订）》（玉政发〔2023〕20号）文件精神，现就组织开展2023年度工业企业购置工业机器人专项奖励资金的申报，现就有关事项通知.',0,'https://www.yuhuan.gov.cn/art/2024/11/12/art_1229303414_4007698.html',NULL,'80751031','',0,0,4,NULL,'经济和信息化局',NULL,'全市范围内注册的各类工业企业',NULL,0,NULL,'全市范围内注册的各类工业企业，开展含机器人的企业项目需经市经信局审批、核准、备案确认，且同时具备以下条件：1、项目符合国家、地方的产业政策和行业发展规划，并在我市行政区域内实施。2、本奖励政策所指的工业机器人按GB/T12643-2013标准定义执行（即：自动控制的、可重复编程、多用途的操作机，可对三个或三个以上轴进行编程；可以是固定式或移动式；在工业自动化中使用）。3、企业在2023年1月1日至2023年12月31日期间（以发票开具日期为准）购置的工业机器人。购置时间应在经信备案项目实施期间内。4、项目承担单位应独立核算、依法纳税，有健全的财务管理制度。','（1）玉环市工业企业购置工业机器人申报资料封面（附件1，需盖章）；\n\n（2）项目批复、核准或备案文件；\n\n（3）玉环市工业企业购置工业机器人清单（附件2，需盖章）；\n\n（4）设备照片；\n\n（5）设备购置合同；\n\n（6）设备购置发票；\n\n（7）其他相关材料（如企业更名的工商变更登记等）。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (704,'关于做好2024年度首台（套）重大技术装备保险补偿项目申报工作的通知','2024-11-11','2024-11-15','根据《关于进一步完善首台（套）重大技术装备首批次新材料保险补偿政策的意见》（工信部联重装〔2024〕89 号）、《首台（套）重大技术装备首批次新材料保险补偿政策实施细则（试行）》（工信厅联重装〔2024〕64 号）、《关于组织开展首台（套）重大技术装备保险补偿项目申报工作的通知》（工信厅联重装〔2024〕400 号）（附件 1）和《关于做好2024年度首台（套）重大技术装备保险补偿项目申报工作的通知》要求，现就做好 2024 年度首台（套）重大技术装备保险补偿项目申报工作有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/11/11/art_1229303414_4007476.html',NULL,'80751031','',0,0,1,NULL,'经济和信息化局',NULL,'生产制造业',NULL,0,NULL,'申报装备需满足《首台〈套〉重大技术装备推广应用指导目录（2024 年版）》（附件 2，以下简称《目录》）要求。整机装备原则上按照台（套）数方式支持;  核心系统、关键零部件，重大技术装备关键配套及基础件原则上按照批次数方式支持。其中高端工业母机、电子专用装备、新型农业机械装备、精密仪器仪表等单台（套）装备价值不高的整机装备可按批次数方式支持；航空发动机、船舶发动机等单件价值较高的核心系统、关键零部件等可按台（套）数方式支持。','资格审定申请表、装备销售合同、专利证明材料、第三方检测报告或用户合格证明',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (705,'关于组织申报2024年度共富合作园建设补助资金的通知','2024-11-11','2024-11-14','为支持共富合作园建设，根据《浙江省经济和信息化厅 浙江省财政厅关于杭州市滨江区-泰顺县等18对制造业高质量发展结对促共富示范创建方案的批复》(浙经信投资[2023)106号)、《玉环市经济和信息化局 玉环市财政局关于印发玉环市制造业高质量发展结对促共富省级专项激励资金使用管理办法的通知》(玉经信[2023]114号)和《关于印发《关于共富合作园建设补助的实施细则》的通知》（玉经信[2023]194号）等文件要求，现将组织申报2024年度共富合作园建设补助资金有关事项通知.',0,'https://www.yuhuan.gov.cn/art/2024/11/11/art_1229303414_4007484.html',NULL,'80751037','',0,0,5,NULL,'经济和信息化局',NULL,'在玉环境内注册的独立法人企业，且在仙居县境内投资建设共富合作园',NULL,0,NULL,'1.园区符合玉环市-仙居县制造业高质量发展结对促共富示范创建要求;2.园区产业定位符合玉环市-仙居县结对促共富主导产业;3.园区需取得仙居县相关投资备案，主要用于制造业研发、生产、经营办公，面积不低于50亩;4.园区免费提供5年,不少于1500 平方米/年厂房作为玉环投资项目入驻的孵化平台。','1.项目投资备案表;\n\n2.共富合作园投资协议;\n\n3.投资证明材料及票据;\n\n4.其他相关材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (706,'关于开展 2024 年度首版次软件产品认定工作的通知','2024-10-28','2024-11-06','2024年度我省首版次软 件产品认定工作已启动，现将浙经信软服〔2024〕216 号《浙江省经济和信息化厅关于开展 2024 年度首版次软件产品认定工作的通知》转发给你们，本次首版次软件产品认定工作通知.',0,'https://www.yuhuan.gov.cn/art/2024/10/28/art_1229303414_4001948.html',NULL,'80751017','',0,0,0,NULL,'经济和信息化局',NULL,'在浙江省内依法注册、具有独立法人资格的企事业单位',NULL,0,NULL,'（一）申报单位应同时符合以下基本条件:1.在浙江省内依法注册、具有独立法人资格的企事业单位；2.遵纪守法、诚实守信，近三年未纳入企业失信名单，未出现债务违约，未发生重大产品服务质量事件、重大生产安全事 故等损害消费者和利益相关者权益的风险事件；3.建立现代企业制度，法人治理结构完善，拥有较为先进的 经营管理理念、较为完善的经营管理制度和有效的创新激励机 制，所产生的经济社会效益显著或具有较强发展潜力；4.具有与软件开发相适应的生产经营场所、软硬件设施等开 发环境（如合法的开发工具等）和人才支撑。（二）省内首版次软件产品申报条件：1.申报的软件产品应同时符合以下基本条件:1）产品类别属于《2024年度浙江省首版次软件产品申报指导目录》范围（详见附件1），重点支持基础软件、工业软件等类别，及工业操作系统、集成电路设计软件、人工智能新兴软件等方向；2）该项软件产品具备自主知识产权，获得软件著作权（2022年6月30日以来获得软件著作权，申报单位须为著作权人第一作者；如果属于共有，则须提供共有权利人同意申请的证明）；3）该项软件产品为首次正式发布并进行销售，具有良好市场推广应用前景；4）该项软件产品功能、性能等指标应通过具有 CNAS 和CMA 资质（须包含软件产品类检测能力）认证的第三方检验检测机构的检测；5）该项软件产品研发投入在 100 万元人民币以上。2.符合以下条件的软件产品可优先认定：1）基于国产软硬件生态系统的软件产品；2）该项软件产品功能或性能有重大突破，具有技术领先优势或者打破市场垄断，填补国内空白；3）该项软件产品拥有相关专利或在研发过程中推动了有关标准制定。（三）国内首版次软件产品申报条件：申报国内首版次软件产品除应满足省内首版次软件产品的 基本条件外，须同时满足以下两项或两项以上条件：1.该项软件产品功能或性能有重大突破，处于国内领先或国 内先进地位；2.该项软件产品解决国外同类产品“卡脖子”技术难题，有效 实现国产化替代，填补国内空白；3.该项软件产品取得相关国家科学技术奖；4.该项软件产品入围相关国家级课题；5.该项软件产品基于国产信息技术路线开发或适配国产软 硬件；6.该项软件产品拥有已授权的相关发明专利；7.该项软件产品在研发过程中，参与了有关行业或国家标准 的制定。','符合条件的企业通过“首台（套）提升工程数字化集成系统”（网址：https://stt.jxt.zj.gov.cn/portal/face）申报。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (707,'关于申报2024年绿色化方向玉环市市级财政奖励资金的通知','2024-10-22','2024-10-24','为了进一步促进和鼓励企业绿色化转型，加快推进我市工业经济高质量发展，根据玉环市人民政府《关于促进新时代美丽经济建设的若干意见（2022年修订）》（玉政发〔2022〕26号）的有关规定，现将组织申报2024年绿色化方向玉环市市级财政奖励资金有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/10/22/art_1229303414_3994095.html',NULL,'80751037','',0,0,0,NULL,'经济和信息化局',NULL,'2022年通过省级自愿性清洁生产验收的企业',NULL,0,NULL,'1、2022年通过省级自愿性清洁生产验收的企业。2、如企业在经营过程中存在以下行为之一的，不得享受市财政奖补政策。对提供虚假材料骗取市财政奖补资金的企业，除收回奖补资金外，取消3年内申报市财政奖补资格。①被列入浙江省公共信用信息平台严重失信名单，且未在一年内修复信用的。②上年度亩均论英雄综合评价为D类的。③偷税数额较大或构成逃税罪移送司法机关的。④经司法生效判决认定构成环境犯罪或罚款额度在20万（含）以上的环境违法行为的。⑤出现亡人3人及以上或造成经济损失达500万元以上安全生产事故的。⑥因产品质量、知识产权违法行为被处罚款10万元以上的。3、上一年度企业职工养老保险参保率、缴费基数夯实率未有增幅，暂缓兑现相关财政奖补资金；本年度内达到参保要求的予以兑现，具体参保要求由人社局牵头制定。','（1）财政奖励资金申请表（网上填报）；\n\n（2）工商变更登记材料（如企业更名请提供）\n\n（3）其他相关材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (708,'关于组织开展第六批产业技术基础公共服务平台申报工作的通知','2024-10-22','2024-10-25','根据《工业和信息化部办公厅关于做好第六批产业技术基础公共服务平台申报工作的通知》和《产业技术基础公共服务平台建设管理暂行办法》（工信部科〔2015〕458号）相关要求，为组织好2024年度第六批产业技术基础公共服务平台的申报推荐工作，现将有关事项通知.',0,'https://www.yuhuan.gov.cn/art/2024/10/22/art_1229303414_3994226.html',NULL,'80751031','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'（一）符合国家有关法律、法规、规章和国家未来产业发展政策及相关规定（约束项）。	在中华人民共和国境内依法设立的独立法人单位。	遵守国家法律、法规和规章，未发生由行政执法监督机关确认的违法、违规行为。	无侵犯他人知识产权的行为。（二）自觉接受政府有关部门的指导和监督（约束项）。	积极承担政府部门委托的有关任务。	按要求上报服务平台能力建设和服务运营情况。	政府有关部门监督检查时，如实汇报有关情况。	上报试验检测、信息服务、创新成果产业化发展报告。（三）具有完善的运行机制（约束项）。	具备完善的管理制度。	具有规范的服务流程和服务质量保证措施。	账务状况及运营情况良好。	具有明确的近期及长期发展目标。	在建立合理高效的市场化运作机制的同时，提供必要的公益服务。（四）行业或地区内公信度高、服务面广、具有示范带动作用。	具有相关领域标准、认证认可、检验检测、试验验证、软硬件集成环境开发及适配服务的经历。	具有相关领域知识产权、产业信息、公开数据集、标准、算力等要素供给服务的经历。	具有相关领域的创新成果中试熟化、供需对接、交易、评价等转化和产业化服务的经历。	具有参与国际检测实验室能力比对验证或国际产品认证的条件和经历。	具有开展国际交流合作的良好条件和合作经历。	与行业或区域内相关的机构（联盟、区域组织、商会、科研院所、企业和高校等）具有紧密合作关系。具有对行业或区域相关资源的整合、推广、辐射及带动服务的能力。（五）拥有高水平的专业人员队伍。	拥有高水平技术人才，专职从事相关研究、服务的人数不少于20人，专业服务人员队伍硕士或中级职称以上专业人员的比例不低于60%，近5年专业人员发表过有影响力的论文、专著。（六）具有提供试验验证、软硬适配、创新成果产业化、要素支撑所必需的基础设施。	拥有自有固定的试验检测场地和经营服务场所。	拥有固定的经营服务场所。	配备符合检验检测、试验验证所要求的抽样、测量、试验和分析仪器设备（含软件），或配备软硬件集成开发环境，具有软硬件适配评测工具集，具有在国产化软件环境迁移与重构等能力。	配备相关要素信息采集或处理工具，具备各类要素的存储、检索、分析、预警或可视化展示等功能。	配备试验验证、中试熟化相关的仪器、设备、中试线等，或具有创新成果资源采集系统、成熟度评价系统、成果转化服务平台，具备供需对接、技术交易、成果管理等功能。	具有较大规模的试验检测（含软硬件适配）用例或数据资源。	具有较大规模的公开数据集、知识产权资源数据库或标准数据库等专业数据资源。	具有较大规模的企业需求、创新成果、专家人才、成果转化工具库等数据资源。（七）具有领先的科研能力和服务能力。	检测认证和校准项目已通过实验室资质认定（CMA）、中国合格评定国家认可委员会（CNAS）或国家认证认可监督管理委员会（CNCA）认可。	已入选国家、行业和地方相关领域的示范、试点或资质认定等。','产业技术基础公共服务平台申报书',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (709,'关于申报2024年技术创新玉环市市级财政奖励资金的通知','2024-10-22','2024-10-24','为了进一步促进和鼓励企业创新能力建设，加快推进我市工业经济高质量发展，根据玉环市人民政府《关于促进新时代美丽经济建设的若干意见（2023年修订）》（玉政发〔2023〕20号）的有关规定，现将组织申报2024年技术创新玉环市市级财政奖励资金有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/10/22/art_1229303414_3994096.html',NULL,'80751037','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'1、获评2023年度浙江制造精品称号的企业；2、获得2023年度国内、省内、市级首台（套）产品称号的企业；3、2023年通过评价为省级工业新产品的企业（以新产品评价证书发布时间为准）；4、获评2022年浙江省供应链企业共同体的企业；5、如企业在经营过程中存在以下行为之一的，不得享受市财政奖补政策。对提供虚假材料骗取市财政奖补资金的企业，除收回奖补资金外，取消3年内申报市财政奖补资格。①被列入浙江省公共信用信息平台严重失信名单，且未在一年内修复信用的。②上年度亩均论英雄综合评价为D类的。③偷税数额较大或构成逃税罪移送司法机关的。④经司法生效判决认定构成环境犯罪或罚款额度在20万（含）以上的环境违法行为的。⑤出现亡人3人及以上或造成经济损失达500万元以上安全生产事故的。⑥因产品质量、知识产权违法行为被处罚款10万元以上的。6、上一年度企业职工养老保险参保率、缴费基数夯实率未有增幅，暂缓兑现相关财政奖补资金；本年度内达到参保要求的予以兑现，具体参保要求由人社局牵头制定。','（1）财政奖励资金申请表（网上填报）；\n\n（2）工商变更登记材料（如企业更名请提供）\n\n（3）其他相关材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (710,'关于开展2024年度浙江省制造业首台（套）装备保险补偿申报工作的通知','2024-10-21','2099-12-21','为贯彻落实省委办公厅、省政府办公厅关于深入实施制造业首台（套）提升工程的意见，更好地推动首台（套）产品在“两新”中的规模化应用，按照《浙江省制造业首台（套）提升工程工作指南（试行）》（浙经信装备〔2022〕9号），现就做好2024年度制造业首台（套）装备保险补偿申报工作有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/10/21/art_1229303414_3994036.html',NULL,'80751031','',0,0,0,NULL,'经济和信息化局',NULL,'列入《浙江省首台（套）产品推广应用指导目录（2024年版）》的首台（套）装备生产企业',NULL,0,NULL,'','投保企业通过“首台（套）提升工程数字化集成系统”（网址：https://stt.jxt.zj.gov.cn/portal/face）填写有关申请表，提交企业法人营业执照、正规合同、最终用户接收证明（明确正式交付时间、地点，通过中间方的还需同时提供最终用户接受或使用证明）、保单并加盖保险公司公章、保险机构开具的保险费发票、保险机构出具的全额保费收取证明、投保企业全额交付保费的支出凭证等申报材料（均为PDF扫描件）。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (711,'关于做好2024年度浙江省制造业首台（套）装备应用奖励申报工作的通知','2024-10-22','2024-10-31','为贯彻落实省委办公厅、省政府办公厅关于深入实施制造业首台（套）提升工程的意见，更好地推动首台（套）产品在“两新”中的规模化应用，根据《浙江省制造业首台（套）提升工程工作指南（试行）》（浙经信装备〔2022〕9号），现就做好2024年度制造业首台（套）装备应用奖励申报工作通知。',0,'https://www.yuhuan.gov.cn/art/2024/10/21/art_1229303414_3994035.html',NULL,'80751031','',0,0,0,NULL,'经济和信息化局',NULL,'列入《浙江省首台（套）产品推广应用指导目录（2024年版）》的节能与新能源汽车及零部件、机器人与数控机床、智能电气、医疗器械等四个“415X”先进制造业集群重点领域的首台（套）装备生产企业。',NULL,0,NULL,'首台（套）装备应用奖励为一次性奖励，符合以下条件的，可以申报首台（套）装备应用奖励：（一）用户企业购置应用节能与新能源汽车及零部件、机器人与数控机床、智能电气、医疗器械等四个“415X”先进制造业集群重点领域的首台（套）装备（前三套装备或者首批次装备）且总价超过1000万元。其中，首批次装备是指用户首次使用的同品种、同技术规格参数、同批生产、同批签订合同（原则上间隔时间不超过3个月）的装备产品。（二）申报首台（套）装备交付用户的时间应在首台（套）装备认定之日起，到退出推广应用目录时间之内。','装备生产企业10月31日前通过“首台（套）提升工程数字化集成系统”（网址：https://stt.jxt.zj.gov.cn/portal/face）填写有关申请表，并根据系统要求提交首台（套）装备正规销售合同或销售发票、销售合同全额支付凭证、用户接收证明（明确正式交付时间、地点，通过中间方的还需同时提供最终用户接收或使用证明）、用户企业应用首台（套）装备实现工程化质量提升等申报材料（均为PDF扫描件）。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (712,'关于组织2024年度浙江省首台（套）装备认定工作的通知','2024-10-18','2024-10-31','为贯彻落实省委办公厅、省政府办公厅关于深入实施制造业首台（套）提升工程的意见，落实《浙江省经济和信息化厅 浙江省交通运输厅关于推动综合交通装备首台（套）突破的实施意见》（浙经信装备〔2023〕254号）等文件要求，根据《浙江省制造业首台（套）提升工程工作指南（试行）》（浙经信装备〔2022〕9 号），现就2024年度浙江省首台（套）装备认定有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/10/18/art_1229303414_3993770.html',NULL,'80751031','',0,0,0,NULL,'经济和信息化局',NULL,'制造业企业',NULL,0,NULL,'一）申报企业应具备较强的技术创新能力，原则上要求建有企业技术中心等创新平台，有较强的人才团队。（二）申报装备应符合《浙江省高端装备制造业发展“十四五”规划》有关领域或全省装备制造业年度工作重点方向要求。（三）申报装备通过国家、省级行业主管部门行政许可以及中国合格评定国家认可中心认可的第三方实验室和检验检测机构的检测。属于国家有特殊行业管理要求的产品，必须具有相关行业主管部门批准颁发的产品生产许可证；属于国家实施强制性产品认证的产品，必须通过强制性产品认证。医疗器械产品应同时提供产品技术要求和全性能检测报告。（四）申报企业应提供近两年重大工程应用或产业化应用等初步市场业绩证明。（五）申报装备应用新技术原理、新设计构思，在结构、材质、工艺等方面对原有产品率先进行根本性改进，产品的主要技术性能指标取得重大突破。企业依法拥有申报装备的自主知识产权，或者通过依法受让取得知识产权。（六）申报装备须先录入首台（套）工程化攻关项目库，并完成验收评价。（七）浙江省制造业首台（套）装备分为国际、国内和省内首台（套）装备三个档次。装备技术水平应达到国内领先及以上，其中申报国内首台（套）应达到国际先进及以上水平，申报国际首台（套）应达到国际领先水平。申报装备应提供国家一级查新资质机构出具的近两年的产品查新报告。（八）存在如下情形的装备不得申报：1.不符合国家和省产业政策导向，属于《产业结构调整指导目录》限制类和淘汰类的。2.节能降耗、污染排放和资源节约指标未达到国内先进水平的，碳排放强度未达到行业先进水平的。3.质量不稳定或出现质量问题在用户中造成较大影响的。4.近三年申报装备已获得首台（套）装备认定，且申报装备无重大技术突破的。','申报企业登录浙企之家（https://zj87.jxt.zj.gov.cn/），通过浙企办-浙企装备-首台（套）系统，于2024年10月31日前申报，逾期系统将关闭。国防科工相关产品需在首台（套）系统提交脱密脱敏材料的同时，通过地方国防科技工业主管部门审核后向省国防科工办报送涉密纸质材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (713,'关于拨付玉环市2023 年度数字经济产业数字化项目财政补助资金的事前公示','2024-10-08','2024-10-15','据《玉环市人民政府关于促进新时代美丽经济建设的若干意见（2023 年修订）》（玉政发〔2023〕20号）有关规定，现对我市2023 年度数字经济产业数字化项目的企业进行补助，现予以公示，公示期7天。如有问题，请来电反映。',0,'https://www.yuhuan.gov.cn/art/2024/10/8/art_1229303414_3991628.html',NULL,'80751055','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (714,'关于2023年度玉环市工业企业综合评价结果的公示','2024-09-20','2024-09-27','根据《玉环市人民政府办公室关于深化“亩均论英雄”改革的实施意见》（玉政发〔2019〕16号）文件精神和《玉环市持续深化“亩均论英雄”改革2024年工作要点》（玉亩均办〔2024〕2号）通知，对全市2023年度的规模上工业企业和规模下工业企业开展“亩均效益”综合评价，经企业申报、用地面积公示，现将我市2023年度工业企业综合评价结果予以公示。',0,'https://www.yuhuan.gov.cn/art/2024/9/20/art_1229303414_3984805.html',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (715,'关于组织开展2024年浙江省企业技术中心认定和评价工作的通知','2024-09-04','2024-09-25','为深入贯彻落实省委办公厅、省政府办公厅《关于强化企业创新主体地位 加强产业科技创新体系建设的意见》，根据《浙江省企业技术中心管理办法》（浙经信技术〔2019〕128号）（以下简称《管理办法》），经商有关部门，现就组织开展2024年企业技术中心认定和评价工作有关事项通知。',0,'https://www.yuhuan.gov.cn/art/2024/9/4/art_1229303414_3964536.html',NULL,'123568954','',0,0,0,NULL,'经济和信息化局',NULL,'不限',NULL,0,NULL,'申报省级企业技术中心的企业原则上需要在2023年8月31日前完成市级企业技术中心的认定，且满足《管理办法》与《评价规范》中规定的基本条件和有关要求。','申报省级企业技术中心的企业通过浙江政务服务网搜索“省级企业技术中心认定”，点击“在线办理”并进行登录（https://oauth.zjzwfw.gov.cn/login.jsp），选择浙江省、申报类型（认定）后填报申报材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (716,'关于开展2024年浙江省未来工厂梯次培育项目征集和试点企业申报工作的通知','2024-08-26','2024-09-06','聚焦“415X”先进制造业集群，围绕精益制造、集成优化、创新引领能力要求，征集一批数字化车间、智能工厂梯次培育项目，择优遴选一批未来工厂试点企业。加强对培育项目和试点企业建设指导，推进“数字化车间—智能工厂—未来工厂”梯次建设。',0,'https://www.yuhuan.gov.cn/art/2024/8/26/art_1229303414_3962112.html',NULL,'80751016','林贝贝',0,0,0,NULL,'经济和信息化局',NULL,'依法登记注册、具有独立法人资格或为在我省依法纳税的非独立法人机构',NULL,0,NULL,'（一）申报主体基本条件。申报主体应依法登记注册、具有独立法人资格或为在我省依法纳税的非独立法人机构，申报项目应在省内建设实施。要求企业生产运营和财务状况良好，近2年内没有违法行为，未发生安全、质量或环境等方面的重大问题（以企业信用核查结果为准，申报未来工厂试点企业信用要求良好以上，申报智能工厂、数字化车间培育项目企业信用要求良好以上）。（二）数字化车间建设要求及培育项目征集条件。数字化车间要求以生产对象所要求的工艺和设备为基础，以信息技术、自动化、测控技术等为手段，用数据连接车间不同单元，对生产运行过程进行规划、管理、诊断和优化。具体请参照《数字化车间通用技术要求》（GB/T37393-2019）和智能制造能力成熟度评估1-2级要求。在建和建成项目均可申报数字化车间培育项目，在建项目原则上要求在2025年10月底前可建成投产，建成项目原则上应为2022年以后完成建设。要求建成前三年内的软硬件总投资（包括企业设备购置、软件购置和其他技术咨询与服务费，不包含土建和厂房投资，下同）不低于2000万元，山区26县原则上不低于1500万元。（三）智能工厂建设要求及培育项目征集条件。智能工厂要求在数字化车间的基础上，通过系统集成、数据互通、人机交互、柔性制造以及信息分析优化等手段，实现对多个数字化车间的统一管理与协调生产。具体请参照国家标准《智能工厂通用技术要求》（GB/T41255-2022）和智能制造能力成熟度评估2-3级要求。在建和建成项目均可申报智能工厂培育项目，在建项目原则上要求在2025年10月底前可建成投产，建成项目原则上应为2022年以后完成建设。要求建成前三年内的软硬件总投资不低于4000万元，山区26县原则上不低于3000万元。（四）未来工厂建设要求及试点企业申报条件。未来工厂应在满足智能工厂的能力要求上，围绕企业未来发展战略和核心竞争力提升的目标，按照“五化四型十场景”的理念、方法和能力要求进行整体规划和设计，融合新一代信息技术，深化数字化设计、智能化生产、精益化管理、绿色化制造、高端化产品、智慧供应链、个性化定制、网络化协同、服务化延伸、模型化发展等场景建设应用（分为必选场景和可选场景，具体要求见附件2）。具体请参照《“未来工厂”建设导则》（DB33/T1388-2024）和智能制造能力成熟度评估3-4级要求。未来工厂试点应以正在实施的新建或扩建项目为主进行申报，原则上要求在2025年10月底前建成投产，建成前三年内累计软硬件总投资不低于8000万元，山区26县不低于6000万元。（五）对标建设及成效要求。企业应结合自身智能制造基础，选择数字化车间、智能工厂或未来工厂作为建设目标进行申报。项目建成后应在提高生产效率、降低运营成本、缩短研制周期、降低产品不良率、提高能源利用效率等方面取得预期成效。未来工厂建成后应在关键技术装备方面取得创新突破，在业态模式创新、组织管理创新、对外赋能路径探索等方面形成特色成果，具有先进性、引领性，并同意对外宣传展示、参观考察和复制推广。','符合条件的企业需于2024年9月6日前，通过企业法人账号登录“浙企智造在线”（https://xzz.jxt.zj.gov.cn）进行申请，填报项目信息，并按要求准备相关材料，同时根据建设方案和预期建设目标完成“未来工厂”在线分级自评估。',NULL);
