insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (730,'2024年企业一次性育才补助资金','2025-01-23','2025-12-31','根据《关于印发的通知》（南委人才办〔2022〕5号）、《关于印发的通知》（南委人才办〔2020〕3号）和《关于加快人才补贴资金兑现的实施意见》（南委人才办〔2022〕3号），将企业一次性育才补助资金拨付到各相关单位，并要求其按规定用途使用，提高人才资金使用绩效。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-24%2F1737682272638.docx&wdOrigin=BROWSELINK',NULL,'82061860','南湖区人才交流服务中心',0,0,0,NULL,'南湖区人力社保局',NULL,'南湖区注册合规企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (731,'2024年市领军人才项目区级补助资金','2025-01-14','2025-12-31','根据《“星耀南湖”领军人才计划实施办法》（嘉人社〔2022〕64 号）、《“星耀南湖”领军人才计划资金拨付管理暂行办法》（嘉委人才办〔2023〕13号）、《关于下达2024年市级人才专项资金任务清单（第三批）的通知》（嘉人社〔2024〕38号）等文件规定，补助人才专项补助资金拨付到各相关单位，并要求其按规定用途使用，提高人才资金使用绩效。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-20%2F1737334614399.doc&wdOrigin=BROWSELINK',NULL,'82061861','南湖区人才交流服务中心',0,0,0,NULL,'南湖区人力社保局',NULL,'南湖区注册合规企业',NULL,0,NULL,'本次补助对象为入选嘉兴市领军人才的项目，经实地走访、审计，确认资金补贴金额。其中，（一）创业人才项目。创业团队项目按A+类1000万元、A类800万元、B类500万元、C类300万元、D类200万元给予项目资助。项目资助分为创业启动资助、创业考核资助和创业奖励资助（仅针对A和A+类项目）。创业领军人才签订三方落户协议，符合条件后，根据企业启动运营评估情况拨付创业启动资助，经绩效评估确认实现协议约定相关产业化预期目标后拨付第二笔创业考核资助。A和A+类项目先按照500万元标准分两笔发放，经绩效评估确认达成协议约定相关产业化预期目标后，可在拨付创业考核资金后拨付剩余资金作为创业奖励资助。（二）创新人才项目。创新人才项目按年度补助。对入选人才到岗后与用人单位签订聘用合同，并通过拨付评估后，按人才年薪的A类70%、B类60%、C类50%给予用人单位引才补助。青年创新人才项目每年最高给予A类80万元、B类50万元、C类30万元补助），补助时间不超过3年。','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (732,'关于推进诸暨市低空经济高质量发展的实施意见','2025-01-16','2025-12-31','低空经济是以各种有人驾驶和无人驾驶航空器的各类低空飞行活动为牵引，辐射带动相关领域融合发展的综合性经济形态。为贯彻落实党中央、国务院以及各级政府有关低空经济发展的决策部署，抢抓低空经济发展新赛道，加快培育新质生产力，打造县域低空经济发展高地，特提出如下实施意见。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530406271.docx&wdOrigin=BROWSELINK',NULL,'0575-87028020','市府办',0,0,0,NULL,'诸暨市政府办公室',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (733,'关于下达2025年度“尖兵领雁+X”科技计划第一批项目的通知','2025-01-10','2025-12-31','根据《浙江省科技创新规划和科技计划管理办法（试行）》《浙江省重点研发计划暂行管理办法》等文件精神，现将2025年度“尖兵领雁+X”科技计划第一批项目下达给你们。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530351431.docx&wdOrigin=BROWSELINK',NULL,'0575-87025731','科学技术局',0,0,0,NULL,'诸暨市科学技术局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (734,'做好2024年度外经贸发展奖励资金（信保补助）申报工作的通知','2025-01-09','2025-02-28','为支持我市经济高质量发展，根据《诸暨市人民政府办公室关于印发等六个政策的通知》（诸政办发〔2024〕9号）等有关文件精神，现就有关申报事项作如下通知',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737531028378.docx&wdOrigin=BROWSELINK',NULL,'87105819','商务局',0,0,0,NULL,'诸暨市商务局',NULL,'注册在本地的外贸企业',NULL,0,NULL,'1、申报企业须为注册在本地的外贸企业；2、申报企业未在逃税骗税、恶意欠薪、故意侵犯知识产权或者在食品药品、生态环境、工程质量、安全生产、消防安全等领域存在违法行为，未被有关部门查处并列入严重失信名单；','本政策适用全市范围内的外贸企业。企业申报材料均在网上（网址：https://reward.sx.gov.cn/）提交、审核：\n\n步骤一：前置准备\n\n浏览器请使用谷歌浏览器或火狐浏览器等新浏览器，若使用双核浏览器请切换至极速模式，而非兼容模式。\n\n步骤二：通过网址注册法人账号\n\n步骤三：登录法人账号，完善相关信息，进行手机号码验证。\n\n步骤四：点击“申报大厅”，“适用地区” 位置点击选择“诸暨市”，申报主体选择“企业”，政策类别选择“开放型经济”。\n\n步骤五：短期出口信用保险:选择“诸暨市对自行投保出口信保企业的补助”，点击“我要申报”，填完表单后点提交。若仍需修改，可点暂存。表单中申报项目内容（写：2024年度短期出口信用保险的财政补助）、证明材料（上传：1、合同复印件；2、付款凭证（含有财务章）；3、发票复印件）有疑问请咨询外贸外经\n\n科室。2024年度投保信用保险申报QQ群号：1034422957，加群请备注企业名，进群后修改群名片为企业名+姓名。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (735,'关于公布2023年度浙江省大型科研仪器开放共享评价结果的通知','2025-01-08','2025-12-31','根据《浙江省大型科研仪器设备开放共享绩效评价办法（试行）》（浙科发基〔2020〕71号），省科技厅会同省财政厅、省教育厅组织开展了2023年度浙江省大型科研仪器开放共享（以下简称“大仪”）评价工作，经材料审查、会议评审和实地核查，对各单位大仪开放共享的组织管理、运行使用和共享服务等情况开展评价。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530471559.docx&wdOrigin=BROWSELINK',NULL,'0575-87025731','科学技术局',0,0,0,NULL,'诸暨市科学技术局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (736,'公布制造业和高技术服务业领域2024年浙江省企业技术中心名单','2025-01-08','2025-12-31','根据《浙江省企业技术中心管理办法》（浙经信技术〔2019〕128号）和《关于组织开展2024年浙江省企业技术中心认定和评价工作的通知》（浙经信高新便函〔2024〕84号）要求，经企业申报、各地推荐、第三方初评、省经信厅组织专家评审并予以公示无异议，现公布杰华特微电子股份有限公司企业技术中心等172家2024年制造业、高技术服务业领域浙江省企业技术中心（名单见附件1）。同意温州工正智能设备有限公司企业技术中心、浙江菲达环保科技股份有限公司企业技术中心主体变更（名单见附件2）。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530969366.docx&wdOrigin=BROWSELINK',NULL,'0575-87011828','经济和信息化局',0,0,0,NULL,'诸暨市经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (737,'关于公布2024年度认定（备案）省级企业研发机构名单的通知','2025-01-07','2025-12-31','根据《浙江省重点企业研究院建设与管理办法》《浙江省企业研究院建设与管理办法》《浙江省高新技术企业研究开发中心建设与管理办法》，经申报推荐、专家评审、网上公示等环节，现将浙江省红外探测技术重点企业研究院等73家认定省重点企业研究院、浙江省驰拓自旋电子器件与技术企业研究院等262家认定省企业研究院、浙江省榜置科技数字流程自动化高新技术企业研究开发中心等1114家备案省高新技术企业研发中心予以公布。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530671384.docx&wdOrigin=BROWSELINK',NULL,'0575-87011828','经济和信息化局',0,0,0,NULL,'诸暨市经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (738,'关于公布2024年度省级数字化服务商及分类评定名单的通知','2025-01-06','2025-12-31','为贯彻省委、省政府关于推进实数深度融合的决策部署，落实制造业数字化转型行动方案，根据《关于组织开展2024年度浙江省数字化服务商分类评定申报工作的通知》要求，经企业自愿申报、地方审核推荐、专家评审和网上公示等相关程序，确定鼎捷数智科技股份有限公司等87家单位（场景型48家、行业型35家，综合型4家）为2024年度省级数字化服务商；',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530554842.docx&wdOrigin=BROWSELINK',NULL,'0575-87011828','经济和信息化局',0,0,0,NULL,'诸暨市经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (739,'关于公布2024年度浙江省科技型中小企业备案名单的通告','2025-01-06','2025-12-31','根据《浙江省科技型中小企业认定管理办法》（浙科发高〔2016〕88号），经形式审查，同意将各市科技型企业主管部门认定的杭州凌坤自动化设备有限公司等21947家企业备案为2024年度浙江省科技型中小企业。同时，撤销一批不再符合认定条件的浙江省科技型中小企业（名单另行下发各地）。',0,'https://view.officeapps.live.com/op/view.aspx?src=https%3A%2F%2Fhqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn%2F%2Fxzz%2Fpublic%2F2025-01-22%2F1737530618217.docx&wdOrigin=BROWSELINK',NULL,'0575-87011828','经济和信息化局',0,0,0,NULL,'诸暨市经济和信息化局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (740,'优质中小企业梯度培育','2024-08-29','2025-12-31','企业生产经营贷款利息补助，对 2023 年度新认定的省创新型中小企业、高新技术企业、省级隐形冠军企业、省级科技“小巨人”企业、国家级专精特新“小巨人”企业（不含重点“小巨人”）等 5 类企业（名单见附件 3）在 2023 年度新增的生产经营类贷款给予贴息补助。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-11-04/1730689933176.pdf',NULL,'0578-6834555','企业科',0,0,0,NULL,'青田县经济商务局',NULL,'青田县注册合规企业',NULL,0,NULL,'2023 年度新认定的创新型中小企业、高新技术企业、省级隐形冠军企业、省级科技“小巨人”企业、国家级专精特新“小巨人”企业（不含重点“小巨人”）等5类企业（附件3）','（一）优质企业梯度培育\n1.资金补助申请表；\n2.贷款合同和银行利息支付凭证材料；\n3.企业营业执照；\n4 真实性声明承诺。\n（二）专精特新中小企业对标提升服务\n1.省级专精特新中小企业诊断咨询报告\n2.省级专精特新中小企业对标提升方案\n3.省级专精特新中小企业对标提升申报表',NULL);
