insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (743,'加快工业项目建设进度奖励','2025-01-24','2025-12-20','2024 年 7 月 1 日以来在庆元县中心城区范围内落地实施并符合奖励条件的民间投资工业项目。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2025-02-21/1740105260822.pdf',NULL,'13185786609','投资与信息化科',0,0,0,NULL,'庆元县经济商务局',NULL,'庆元县中心城区范围内落地实施并 符合奖励条件的民间投资工业项目',NULL,0,NULL,'2024 年7月1日以来在庆元县中心城区范围内落地实施并符合奖励条件的民间投资工业项目。','2024 年 7 月1日起办理工程规划许可证且符合奖励政策条\n件的工业项目，按照申报类别向县经济商务局提供以下佐证资\n料，经县住建局初审，县经商局审核确认后兑现。\n1.庆元县工业项目建设进度奖励申请表；\n2.国有土地出让合同、无法提供土地出让合同的提供相关\n说明；\n3.项目批复文件或备案表；\n4.《建设工程规划许可证》；\n5.《建筑工程施工许可证》；\n6.固定资产投资报表；\n7.不动产权证（针对改扩建项目）；- 3\n8.标准地履约合同；\n9.竣工验收报告；\n10.建设工程规划核实确认书及其附件；\n11.房屋建筑和市政基础设施工程竣工验收备案表；\n其他要求提供的资料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (744,'庆元县科学技术局关于印发\n《庆元县企业研发经费投入后补助实施办法》\n的通知','2024-11-22','2025-12-31','基础补助金额按企业年度补助基数的 2%给予补助，非国家高新技术企业不超过20万元，国家高新技术企业不超过30万元；增量补助金额按企业年度补助基数较前一年度基数增量部分的5%给予补助，非国家高新技术企业不超过 30 万元，国家高新技术企业不超过 50 万元。以上金额均精确到“元”，增量补助金额为负数的以“0 元”计算。设立“研发贡献奖”，用于奖励当年度研发费用增量、增速分别排名前 5 企业的财务负责人和技术负责人，奖励标准为3000 元/人。增量、增速排名前 5 企业出现重合时，奖励可重复享受。',0,'https://hqzc-oss.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn//xzz/public/2024-12-03/1733206153975.pdf',NULL,'0578-6056309','综合计划科',0,0,0,NULL,'庆元县科学技术局',NULL,'在庆元县注册且纳入国家科技统计调查的规模以上工业企业',NULL,0,NULL,'（一）国家高新技术企业须当年度所得税汇算清缴达到国家高新技术企业认定条件中研发费用比例要求（未达到要求的不列入补助范围）。（二）纳入国家科技统计调查的企业，按照统计制度如实填报。（三）被列入严重失信行为名单的企业，补助申请不予受理。','1.《补助申请表》;\n 2.企业12月份财务状况表（高新技术企业同时提交近两年\n度报送税务部门的《中华人民共和国企业所得税年度纳税申报表\n（A类）》主表及《研发费用加计扣除优惠明细表》附表）;\n 3.企业营业执照复印件;\n 4.当年度研发费用增量、增速排名前5的企业提交《企业财\n务负责人和技术负责人信息表》。\n5、无严重失信行为承诺书',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (745,'关于做好2025年度宁波市首台（套）装备申报工作的预通知','2025-02-18','2025-02-27','2025年度宁波市首台（套）装备申报工作将于8月份正式启动，请通知有意向的企业仔细阅读附件1预通知，提前开始准备申报材料（材料清单见附件1），并填写附件2《2025年度宁波市首台（套）装备申报摸底情况汇总表》，于2月27日前将电子版报送区经信局，联系人：曹老师，联系电话：89384563。',0,'https://www.bl.gov.cn/art/2025/2/18/art_1229055347_4597003.html',NULL,'89384563','曹老师',0,0,0,NULL,'北仑区经信局',NULL,'在宁波市行政区域内注册一年及以上的企业，近三年未发生重大安全、环保、质量等事故，未列入严重失信名单，具有装备产品技术创新和制造能力，研发基础条件较好，拥有专业且稳定的技术人才队伍。',NULL,0,NULL,'1.符合支持行业范围。申报产品应符合国家、省、市装备产业发展规划列明的有关领域或浙江省高端装备制造业统计分类目录。2.取得重大技术突破。申报单位经过技术创新活动，运用新技术、新设计等手段，在结构、性能、材质、工艺等方面率先取得根本性突破，综合技术性能指标达到国内领先水平及以上的新产品。3.拥有自主知识产权。申报单位掌握产品生产的关键核心技术、关键工艺，在国内外依法拥有自主知识产权和自主品牌，包括依法受让取得知识产权的所有权或使用权。4.通过科技查新和鉴定(评价)验收。申报产品应提供近两年鉴定(评价)验收证明和国家一级查新资质机构出具的产品查新报告。鉴定(评价)验收可以是以下三者之一:市级及以上行业主管部门的工业新产品鉴定(评价)验收、浙江省制造业首台(套)工程化攻关项目鉴定(评价)验收、省级及以上行业协会(学会)组织的产品鉴定(评价)验收并获宁波市及以上科技成果登记机构出具的相应科技成果登记证书。申报国际首台(套)的，应通过国际科技查新，综合技术达到国际领先水平;申报国内首台(套)产品的，应通过国际科技查新，综合技术达到国际先进水平及以上;申报省内首台(套)产品的，应通过国内或国际科技查新，综合技术达到国内领先水平及以上。5.通过检测认证。申报产品应通过国家认证认可监督管理部门或省级质量技术监督部门计量认证合格的第三方实验室或检验机构的检测验证。属于国家有特殊行业管理要求的产品，须具有相关行业主管部门批准颁发的产品生产许可证;属于国家实施强制性产品认证的产品，须通过强制性产品认证。6.市场前景好。申报单位具备产品设计及主要关键部件的制造能力，且申报产品已在相关领域应用，能够提供近两年至少一个应用成功案例。投入产业化应用后能够实现批量化生产和销售，满足售后客户要求，社会和经济效益显著','1.必要材料\n(1)首台(套)装备申报表;\n(2)首台(套)装备申请报告书;\n(3)申报材料真实性承诺声明;\n(4)单位的法人营业执照副本复印件:\n(5)宁波市及以上行业主管部门的工业新产品评价验收证书，或浙江省制造业首台(套)工程化攻关项目鉴定验收证书或省级及以上行业协会(学会)组织的产品鉴定验收证书并获宁波市及以上科技成果登记机构出具的相应科技成果登记证书;(6)产品知识产权权益状况和品牌状况的有效证明文件涉及多个单位的，应提交与产品技术归属及权限有关技术转让、\n许可、授权、合作生产、合作开发合同或协议;(7)具有资质的第三方实验室或检测机构出具的产品检测验证报告;特殊行业产品生产许可证、强制性产品认证证书;(8)国家一级资质机构出具的产品科技查新报告;(9)审计机构或会计师事务所出具的企业上年度财务审计报告(含报表、纳税及研发投入情况说明)\n(10)申报国际首台(套)认定的，提供至少一名院士或者两名浙江省特级专家及相当层次专家推荐信;(11)人才情况及相关社保缴纳证明;(12)产品销售合同、销售发票、用户单位使用评价报告;(13)产品照片或设备现场生产视频资料。\n2.非必要补充证明材料\n(13)申报产品的标准备案资料，以及企业参与各级、各类标准制定的证明资料;(14)与申报产品研发相关的发表论文;(15)获得的相关荣誉证书、其它有关证明材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (746,'关于列入2024年度北仑区技术改造项目计划的公示','2025-02-19','2025-02-26','根据《北仑区经济和信息化局 北仑区财政局关于印发<北仑区技术改造项目和智能制造项目管理实施办法（试行）>的通知》（仑经信〔2022〕65号）和《关于组织申报2024年度北仑区技术改造项目的通知》（仑经信〔2024〕68号）等文件要求，经企业申报、北仑区经信局会同区财政局组织专家对各企业申报的项目进行专家评定，现确定60个项目列入2024年度北仑区技术改造项目计划，现予以公示（详见附件）。如有不实之处，请于2025年2月26日前向区经济和信息化局反映，联系电话：89384615。',0,'https://www.bl.gov.cn/art/2025/2/19/art_1229055347_4597516.html',NULL,'89384615','经信局',0,0,7,NULL,'北仑区经信局',NULL,'北仑区注册合规企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (747,'关于组织开展北仑区第七批专精特新“小巨人”申报培训会的通知','2025-02-17','2025-02-19','为更好地解读“专精特新”政策，切实做好2025年第七批国家级专精特新“小巨人”企业申报工作准备，帮助企业进一步完善申报材料，决定召开北仑区第七批专精特新“小巨人”申报培训活动，特邀请“小巨人”申报资深专家和市经信局相关工作负责人至北仑为企业就申报国家级专精特新“小巨人”事宜现场答疑解惑，开展面对面精准评估辅导。',0,'https://www.bl.gov.cn/art/2025/2/17/art_1229055347_4596691.html',NULL,'89384559','北仑区经济和信息化局中小企业发展科，刘老师',0,0,3,NULL,'北仑区经信局',NULL,'有意向申报第七批专精特新“小巨人”且已基本完成申报材料筹备的企业。',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (748,'关于组织开展“两新”领域用能设备更新改造项目申报（重点用水设备更新改造）工作的通知','2025-02-14','2025-02-20','根据国家和浙江省关于“两新”领域用能设备更新改造项目申报工作相关要求，经信部门主要配合发改部门，组织开展冷却塔等重点用水设备更新改造项目申报（超长期国债支持），主要涉及电力、石化、化工、钢铁、纺织印染、造纸、食品医药、数据中心等重点用水行业，用水设备更新改造项目的年节水量须不低于 50 万立方米。请各街道、功能区通知所属企业，确保重点用水行业企业都通知到位。',0,'https://www.bl.gov.cn/art/2025/2/14/art_1229055347_4596297.html',NULL,'89384570','陈老师',0,0,3,NULL,'北仑区经信局',NULL,'重点用水行业企业',NULL,0,NULL,'以工业园区、产业集群为载体，整体部署并规模化实施的设备更新项目（以下简称“联合申报项目”），总投资应不低于2000万元；联合申报项目申报主体原则上应为园区管委会、国有平台公司、节能/节水服务企业等具备项目管理权限或资质的第三方单位，子项目单位不得作为申报主体；申报项目及其子项目均须履行项目审批（核准、备案）手续，申报项目建设内容应覆盖各子项目建设内容，各子项目应按照前述要求履行各项前期手续；申报主体负责整体项目资金申请报告编制工作，须严格按照资金申请报告模版编制（详见附件1），分别列出各子项目建设内容、设备更新改造清单、总投资、核定投资补助基数、超长期特别国债资金需求、年节能量/年节水量、资金拼盘及开工要件等信息，并提供详细的投资估算表；申报主体须出具承诺函，明确不参与超长期特别国债资金分配，不在项目组织过程中向子项目建设单位收取服务费、管理费、中介费等不合理费用；各子项目建设单位须出具承诺函，明确对申报超长期特别国债及后续补助资金分配不持异议。','资金申请文件、资金申请报告、项目情况汇总表（详见附件）的纸质和电子版',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (749,'关于开展浙江首批次认定项目排摸工作的通知','2025-02-10','2025-03-10','为对接2025年度浙江省首批次新材料认定工作，建立首批次新材料产品申报项目库，现开展浙江省重点首批次新材料产品排摸工作，请各地及相关企业认真排摸，按要求填写汇总表（附件1），并于3月7日前发送至电子邮箱：1712147558@qq.com',0,'https://www.bl.gov.cn/art/2025/2/10/art_1229055347_4595023.html',NULL,'89292044','潘超林',0,0,1,NULL,'北仑区经信局',NULL,'材料类企业',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (750,'关于组织开展2024年度打造新能源汽车之城专项政策申报工作的通知','2025-02-08','2025-12-31','为贯彻落实《关于加快打造新能源汽车之城的若干意见》（甬政办发〔2023〕27号）文件精神，支持企业做大做强，提升发展新能源及智能网联汽车产业集群，现就组织开展2024年度打造新能源汽车之城专项政策申报工作有关事项通知见附件。',0,'https://www.bl.gov.cn/art/2025/2/8/art_1229055347_4594508.html',NULL,'89384563','曹老师',0,0,0,NULL,'北仑区经信局',NULL,'北仑区注册合规企业',NULL,0,NULL,'（一）我市依法注册登记、依法纳税、依法缴纳社保、具有独立法人资格的企事业或社会组织。（二）近三年以来未发生过重大安全、环保、质量事故，或被列入严重失信名单。','申报单位营业执照复印件，《打造新能源汽车之城专项政策资金申请表》（附件1，以下简称“资金申请表”），检测资质证明材料，2024年度财务审计报告或第三方专项财务审\n\n计报告，2024年12月增值税纳税申报表，2024年汽车及零部件检测发票开票清单（附件2）及发票，其他有关材料。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (751,'关于开展工业资源综合利用先进适用工艺技术设备申报的通知','2025-02-07','2025-03-03','根据《三部门关于开展〈国家工业资源综合利用先进适用工艺技术设备目录〉推荐工作的通知》有关要求，现组织开展工业资源综合利用先进适用工艺技术设备申报工作，申报范围包括工业固废源头减量、综合利用、再生资源利用及机电产品再制造等领域。',0,'https://www.bl.gov.cn/art/2025/2/7/art_1229055347_4594168.html',NULL,'89384570','陈老师',0,0,2,NULL,'北仑区经信局',NULL,'北仑区注册合规企业',NULL,0,NULL,'(一)推荐的工艺技术设备主要面向工业固废源头减量、工业固废综合利用、再生资源综合利用和机电产品再制造等四个领域。(二)推荐的工艺技术设备应符合国家法律法规、产业政策和相关标准要求，主要指标具有先进性，经济、环境、社会效益明显，知识产权或专有技术产权明晰，已实现产业化应用并实际运行超过1年。推荐的工艺技术设备应与《国家工业资源综合利用先进适用工艺技术设备目录(2023年版)》进行对照，同类技术的相关技术指标应有所提升。','1.工艺技术设备推荐汇总表\n2.工业资源综合利用先进适用工艺技术设备申报书\n3.工艺技术设备应用实例表',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (752,'2025年度宁波市甬江人才工程（引进类）公告','2025-02-05','2025-12-31','为加快建设世界重要人才中心和创新高地战略支点城市，奋力打造高水平人才首选地、创新策源地、产业集聚地，更大力度吸引天下英才与宁波共成长，现将2025年度甬江人才工程（引进类）（以下简称“甬江人才工程”）公告',0,'https://enb.ningbo.gov.cn/home/publicDetail/472',NULL,'123568954','',0,0,1,NULL,'宁波市科学技术局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (754,'2024年西湖区加快算力产业发展政策资金','2024-12-26','2025-12-31','根据《关于印发<西湖区加快算力产业发展的若干措施（试行）>的通知》（西发改经信〔2023〕57号），现下达2024年西湖区加快算力产业发展政策资金。',0,'关于下达2024年西湖区加快算力产业发展政策资金的通知',NULL,'0571-89513715','胡明珏',0,0,0,NULL,'西湖区经信局',NULL,'算力企业',NULL,0,NULL,'1、算力服务平台建设补助企业自主投资建设公共算力服务平台并对外输出算力服务，服务企业数达到100家(含)以上。2、资源对接、论坛赛事活动补助支持社会组织和企业开展算力、算法资源对接，相关论 坛、赛事等活动，活动规模超过100家企业或活动宣传流量在500万人/次以上的。3、推进算力赋能产业发展打造西湖区公共算力服务平台，对通过平台使用智算算力的企业，发放总额不超过5000万元的“算力券”','线上申报',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (755,'2024年数字经济高质量发展政策资金','2024-12-18','2025-12-31','根据《关于印发<西湖区关于促进数字经济高质量发展的扶持意见（试行）>的通知》(西发改经信〔2022〕38号)，现下达2024年西湖区数字经济高质量发展政策资金。\n\n\n',0,'根据《关于印发<西湖区关于促进数字经济高质量发展的扶持意见（试行）>的通知》(西发改经信〔2022〕38号)，现下达2024年西湖区数字经济高质量发展政策资金。',NULL,'0571-89513716','胡明珏',0,0,0,NULL,'西湖区经信局',NULL,'数字经济企业',NULL,0,NULL,'1.享受政策的企业，注册地、纳税地必须在西湖区内。2.享受政策的企业，区内政策优惠扶持按“就高、不重 复”原则进行资助(奖励)。同一项目、标准在低等次已作资 助的(奖励)的，晋升到高等次时可补足差额部分；区财政补助后，企业又获得市级以上(含)补助的，区财政补助资 金视作已经配套，市级以上(含)补助资金纳入本意见补助总额。如遇上级政策调整的，以调整为准。除本政策第1条外，单个企业的支持总额以企业上年度区地方经济贡献为限。集成电路政策政府支持资金不超过企业项目全部投入的30%。特殊情况一事一议。3.本政策支持总额以当年度财政预算为限。4.享受政策的企业应无违法违纪行为，信用记录良好。 当年发生安全生产、食品安全、环境污染等重大责任事故和 发生较大群体性事件的，不享受本政策。5.享受政策企业注销、迁出西湖区的，须进行财政、 税收清算，所享受的资助(奖励)全额退还。由属地镇(平台)会同行业主管部门负责各项清算工作。6.符合条件的企业根据申报通知的具体要求，由企业 提出申请，经所属镇街(平台)初审，区发改经信局会同区 财政局等相关部门审核通过后发文执行。7.本意见自发布之日起三十日后施行，有效期三年， 由区发改经信局负责牵头组织实施。原《关于打造西溪谷区 块链产业园的政策意见(试行)》(西金融办(2017)8号)、《西湖区关于促进领军企业发展的扶持意见》(西发改经信〔2019〕39号)、《西湖区关于支持瞪羚企业发展的扶持意见》(西发改经信〔2019〕40号)、《西湖区关于促进数字经济发展推动产业转型升级的扶持意见》(西发改经信 (2019〕41号)同时废止。具体由区发改经信局、区财政局负责解释。8.本意见如与国家、省、市最新法律、法规及政策不一致时，以国家、省、市最新法律、法规及政策为准。','线上申报',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (756,'关于开展杭州市中小企业数字化转型城市试点样本企业选树工作的通知','2025-01-26','2025-06-06','为贯彻落实财政部、工信部《关于开展中小企业数字化转型城市试点工作的通知》（财建〔2023〕117号）、工信部办公厅关于《第一批中小企业数字化转型试点城市实施方案（备案版）》的批复（工信厅企业〔2023〕79号）、《杭州市人民政府办公厅关于印发杭州市中小企业数字化转型城市试点工作方案的通知》、《杭州市中小企业数字化转型城市试点项目及资金管理办法》（杭经信联产数〔2024〕1号）等文件部署要求，引导更多企业“看样学样”，加快数字化转型发展，拟选树一批样本示范企业',0,'https://jxj.hangzhou.gov.cn/art/2025/1/26/art_1229252271_4330038.html',NULL,'85257095','王梦婉',0,0,1,NULL,'杭州市经信局',NULL,'汽车零部件、生物医药及健康、通信设备制造三个行业',NULL,0,NULL,'（一）申报企业须为杭州市汽车零部件、生物医药及健康、通信设备制造三个试点行业企业，具有独立法人资格，运营和财务状况良好，依法纳税，具有良好社会信用。（二）已完成试点数字化改造项目验收，且在制定数字化转型规划，实施基于统一标准的生产设备数字化改造升级和机联网建设，在关键业务领域深度应用数字化系统、打通生产经营全流程数据链，应用“人工智能＋”、“底座＋”等先进技术，形成数据驱动的业务优化和管理创新等方面工作突出，提升企业智能化制造、数字化管理水平明显。（三）企业数字化转型方案，能充分体现行业特点和适用场景，具有较强的代表性、示范性、实效性和创新性,具有较强的借鉴意义和推广价值。','企业编写《杭州城市试点中小企业数字化转型标杆企业申报书》，对所报文件及材料的真实性负责，申报资料装订成册，一式8份纸质件和一份电子件于2025年6月6日前报送所在区、县（市）经信部门，区、县（市）经信部门会同级财政部门审核无异议的，于2025年6月11日前汇总行文，将企业申报材料报送至杭州市经济和信息化局（杭州市数字经济局）〔以下简称：市经信局（市数字经济局）〕产业数字化处。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (757,'杭州市经信局（市数字经济局）关于公布《2024年杭州市首版次软件产品应用推广指导目录》的通知','2025-01-26',NULL,'为贯彻落实《关于做好2024年度首版次软件产品认定推荐\n工作的通知》文件精神，加快杭州市首版次软件产品应用推广，\n经企业申请、区县初审推荐和专家评审，评审结果面向社会公示\n无异议后，确定《2024年杭州市首版次软件产品应用推广指导\n目录》，现予以公布。',0,'https://zjjcmspublic.oss-cn-hangzhou-zwynet-d01-a.internet.cloud.zj.gov.cn/jcms_files/jcms1/web3244/site/attach/0/9a7024e377c44e3c988218bf2cd4e6f4.pdf',NULL,'123568954','',0,0,0,NULL,'杭州市经信局',NULL,'不限',NULL,0,NULL,'','',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (758,'杭州市经信局（市数字经济局）关于公布2024年杭州市优质数字工程服务商名单的通知','2025-01-02',NULL,'根据《杭州市人民政府办公厅关于印发杭州市推进软件和信息技术服务业发展若干政策的通知》（杭政办函〔2022〕61号），经企业自主申报，各区、县（市）推荐，专家评审和网上公示，确定杭州迈的智能科技有限公司等10家企业为2024年杭州市优质数字工程服务商。\n\n希望各地加强对数字工程服务商的培育引导，及时协调解决企业存在的困难和问题，积极搭建供需桥梁，激发数字化转型市场主体活力，培育杭州数字工程服务产业，进一步推动杭州制造业高质量发展，推进新型工业化迈上新台阶。',0,'https://jxj.hangzhou.gov.cn/art/2025/1/2/art_1229252271_4324269.html',NULL,'123568954','',0,0,0,NULL,'杭州市经信局',NULL,'不限',NULL,0,NULL,'','',NULL);
