insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (769,'关于召开全区2024年度科协工作总结交流会议的通知','2025-01-06','2025-12-31','经研究，决定于2025年1月10日（星期五）召开全区2024年度科协工作总结交流会，具体事项通知如下',0,'http://www.nanhu.gov.cn/art/2025/5/13/art_1229282454_2554254.html',NULL,'15257318794','何逸君',0,0,0,NULL,'科技',NULL,'南湖区基层科协',NULL,0,NULL,'','请各单位准备2024年特色亮点工作及2025年重点工作计划书面材料4份携带至会场，材料要求简明扼要。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (770,'2025年南湖区统计工作要点','2025-05-06','2025-12-31','2025年是“十四五”规划收官之年，是谋划“十五五”规划、启航新征程的关键一年，也是南湖区高质量发展建设共同富裕示范区之首善之区的提升之年。2025年全区统计工作的总体思路是：全区统计系统要以习近平新时代中国特色社会主义思想为指导，深入学习贯彻党的二十大和二十届二中、三中全会精神，认真贯彻落实习近平总书记关于统计工作重要讲话和重要指示批示精神，认真落实省、市、区“两会”和各级统计工作会议要求，紧紧围绕区委区政府中心工作，大力倡导“六干”，紧扣统计主责主业，以国家统计督察整改为契机，全面推进党建与业务深度融合，全链条强化数据质量管控，多维提升统计决策服务水平，为聚力建强主城区、提升城市首位度，加快高质量发展建设共同富裕示范区首善之区提供坚实统计保障。',0,'http://www.nanhu.gov.cn/art/2025/5/6/art_1229282864_2553610.html',NULL,'123568954','',0,0,0,NULL,'统计',NULL,'不限',NULL,0,NULL,'','',NULL);
