insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (772,'关于印发2025年中央农业经营主体能力提升（上半年粮油规模种植主体单产提升）项目实施方案的通知','2025-04-25','2025-12-31','现将《2025年中央农业经营主体能力提升（上半年粮\n油规模种植主体单产提升）项目实施方案》印发给你们，请\n结合实际，认真组织实施。',0,'http://www.nanhu.gov.cn/art/2025/4/25/art_1229282679_2553194.html',NULL,'123568954','',0,0,0,NULL,'农业农村',NULL,'从事粮油规模种植的大户、家庭农场、农民合作社等农业经营主体',NULL,0,NULL,'（一）本项目支持对象为种植小麦、油菜100亩及以上的规模主体，且不得与承担省级绿色高产高效行动项目的主体或地块重复。（二）申报主体当年良种覆盖率达到l00％，绿色高产高效技术到位率95％以上。（三）申报主体应加强田间管理，落实高产绿色关键技术，努力提高单产水平。单独建立单产提升项目田问管理档案，全程记录农事操作和投入品使用情况。','申报主体填写《2025年南湖区粮油规模种植主体单产提升行动项曰申报表》并提交相关佐证资料（种子购销证、营业执照复印件、负责人身份证复印件、实施主体土地承包合同等）。',NULL);
