insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (774,'2025年南湖区印刷企业年度报告工作催告通知','2025-02-26','2025-12-31','2025年南湖区印刷企业年度报告工作已于2025年2月25日截止。经多次催告，仍有部分单位未在规定时间内向我局提交企业年度核验材料（名单附后），现特通知以下单位在2025年3月6日前完成年度核验工作。逾期未年检的，我局将根据《国家新闻出版署关于开展2025年印刷企业年度报告和复制单位年度核验的通知》（国新出发函〔2024〕192号）和省、市新闻出版局相关工作要求，将其列入“异常名录库”，对连续三年（含）以上未经年度核验的单位，注销其《印刷经营许可证》。',0,'http://www.nanhu.gov.cn/art/2025/2/26/art_1229282224_2546365.html',NULL,'0573-82838065','',0,0,0,NULL,'其他',NULL,'南湖区印刷企业',NULL,0,NULL,'','',NULL);
