insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (790,'诸暨市民政局关于“爱心卡”服务机构（企业）遴选公告','2025-05-15','2025-05-23','根据《关于全面推行养老服务“爱心卡”制度的实施方案（试行）的通知》（诸政办发〔2025〕9号）工作要求，现面向社会公开遴选我市“爱心卡”服务机构（企业），现将有关事项公告如下',0,'https://www.zhuji.gov.cn/art/2025/5/15/art_1370364_59157101.html',NULL,'0575-87017247','市民政局',0,0,1,NULL,'民政',NULL,'依法设立，具有相应服务团队且年检合格的专业机构（企业）、民政部门依法备案的养老机构。',NULL,0,NULL,'1.具备法人资格。2.在现运营场所正式营业3个月以上。3.规范运营，近一年内（不足一年的自开业以来）未因医疗服务、养老服务等行为受到相关行政部门处罚。4.配备不少于2名“爱心卡”专职管理人员，熟悉“爱心卡”政策规定及要求。5.配备一定数量的专职上门服务人员，且应当符合下列条件之一：（1）执业医师或者护士；（2）长期照护师、养老护理员、康复理疗师、医疗护理员等相关护理人员。6.具有与“爱心卡”政策相适应的服务管理、财务管理、信息统计、内控管理、人员管理、档案管理等制度7.与“爱心卡”服务相关的收费项目和收费价格符合政策规定。申请提供医疗护理服务的机构应当符合卫生健康部门的有关规定。','诸暨市养老服务“爱心卡”服务机构(企业）申请表',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (791,'关于申报2024年度鼓励壮大软件企业奖补的通知','2025-05-13','2025-05-16','为鼓励壮大软件企业，根据《2024年度奉化区服务业政策清单》，开展2024年度鼓励壮大软件企业奖补申报工作，现将有关事项通知如下。',0,'https://enb.ningbo.gov.cn/home/publicDetail/793',NULL,'89286432','周嘉龙',0,0,0,NULL,'经信',NULL,'纳入规上统计（以统计口径为准）的软件企业',NULL,0,NULL,'见政策原文','一、首次上规奖补\n1.软件企业奖补资金申请表（附件）；\n2.申报年企业统计报表（财务状况表）；\n3.申报通知中要求的其他材料。\n二、营收增量突破奖补\n1.软件企业奖补资金申请表（附件）；\n2.申报年及前一年企业统计报表（财务状况表）；\n3.申报年企业研发费用支出统计报表；\n4.申报年税务部门出具的企业年度缴纳相关税收的纳税证明；\n5.申报通知中要求的其他材料。',NULL);
