insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (901,'浙江省人力资源和社会保障厅办公室关于开展 2025年度浙江省人力资源和社会保障课题申报工作的通知','2025-03-14','2025-04-07','为加强人力资源社会保障领域政策理论研究，强化我省人力资源和社会保障事业高质量发展理论支撑，决定自即日起开展2025年度浙江省人力资源和社会保障课题申报工作，现将有关事项通知如下',0,'https://zhengce.zj.gov.cn/policyweb/httpservice/showinfo.do?infoid=5c178824c5d84428bff8b550e862f0f0',NULL,'0571-87972309','浙江省人力资源和社会保障科学研究院 张旭',0,0,0,NULL,'人社',NULL,'各市、县（市、区）人力资源和社会保障局，各有关单位',NULL,0,NULL,'课题负责人应当承担实质性研究工作，具备相关研究储备和条件，且由一人担任。重点课题负责人原则上应当具备副高级（或相当于副高级）以上专业职称，一般课题负责人原则上应当具有中级（或相当于中级）以上专业职称。每位申报者只能主持申报一个项目，不得以已立项或结题的厅级以上课题相同或相近内容申报本次课题。重点课题须严格按照选题指南明确的题目和主要研究内容进行申报，请勿自拟题目或改变研究内容。一般课题的具体题目可由申报人根据选题指南自行拟定，但要避免研究题目和研究内容过于宽泛、偏离主题。','（一）申报者填写《2025年度浙江省人力资源和社会保障课题申报基本信息表》（见附件2）、《2025年度浙江省人力资源和社会保障课题申报活页》（见附件3），经单位审查合格、同意推荐并加盖公章，盖章页以PDF扫描件形式提交，其他表格均提交Word版。\n（二）推荐单位填写《2025年度浙江省人力资源和社会保障课题申报汇总表》（见附件4），提交Word版及盖章后的PDF版。\n（三）申报所需材料请直接登录浙江省人力资源和社会保障网“公告公示”栏目查询并下载（网址：rlsbt.zj.gov.cn)。',NULL);
insert  into `policy`(`id`,`name`,`publish_date`,`expire_date`,`description`,`status`,`content`,`formula`,`phone`,`contact`,`is_money`,`rec_level`,`hot_level`,`money`,`source`,`file_url`,`policy_object`,`is_custom`,`doc_number`,`doc_detail`,`policy_condition`,`policy_doc_detail`,`created_at`) values (902,'浙江省财政厅关于强化制度执行 进一步推动行政事业性国有资产管理提质增效的通知','2025-03-14',NULL,'为深入贯彻落实党中央关于加强财政资源统筹等部署要求，全面强化制度执行，推动资产管理提质增效，根据《财政部关于强化制度执行 进一步推动行政事业性国有资产管理提质增效的通知》（财资〔2024〕155号）相关要求，并结合推进落实《财政部关于依托预算管理一体化系统建立全国行政事业单位国有资产调剂共享平台的通知》（财资〔2024〕175号）的工作部署，以及《全省2024年度公务支出公款消费和行政事业性国有资产管理审计情况》反映的国有资产管理问题，现将有关事项通知如下',0,'https://zhengce.zj.gov.cn/policyweb/httpservice/showinfo.do?infoid=f823eeac6a1b4ebbbc3cfa2002350534',NULL,'123568954','',0,0,0,NULL,'财政',NULL,'各市、县（市、区）财政局（宁波不发），省级各单位',NULL,0,NULL,'','',NULL);
